CREATE PROCEDURE [dbo].[Accounts] AS


DECLARE @qstr nVarchar(4000);
DECLARE @depth INT,@i INT; 
SET @depth=(SELECT MAX(AccDepth) FROM T_Account);

SET @qstr='SELECT T1.accountID,T1.AccountTitle AS Account ,T1.ParentID AS Parent1ID';
SET @i=1;
