	@ProcessID int,
	@SizesID int,
	@ColorsID int,
	@TransactionTypeID INT,
	@TransactionQty INT,
	@UnitID INT,
	@TransactionDate DateTime,
	@OrderID INT,
	--@RefID INT,
	@CompanyID INT,
	@UserID INT
	
AS
	/* SET NOCOUNT ON */
	
	Declare @ProcessRateID INT;
	SET @ProcessRateID=(SELECT dbo.fnGetRateID(@StyleID,@ProcessID,@SizesID,@ColorsID,@CompanyID))
	--Check to Receive
	/*
	IF @TransactionTypeID=2  
	BEGIN
	IF ISNULL((SELECT count(*) FROM VW_TransactionResult WHERE EmployeeID=@EmployeeID AND ProcessRateID=@ProcessRateID AND IssueQty-ReceiveQty >=@TransactionQty ),0)=0
	raiserror('This Same type or quantity production is not issued to this person',16,1);
	END
	*/
	--Check to Damage
	IF @TransactionTypeID=3
	BEGIN
	IF ISNULL((SELECT count(*) FROM VW_TransactionResult WHERE EmployeeID=@EmployeeID AND ProcessRateID=@ProcessRateID AND ReceiveQty >=@TransactionQty ),0)=0
	raiserror('This Same type or quantity production is not received from this person',16,1);
	END
	
	--Check to Damage
	IF @TransactionTypeID=4
	BEGIN
	Declare @RestDamQty INT;
	SET @RestDamQty=ISNULL((SELECT SUM(DamageQty-RecoveryQty) FROM VW_TransactionResult WHERE ProcessRateID=@ProcessRateID AND CompanyID=@CompanyID),0);  
	IF @RestDamQty< @TransactionQty
	raiserror('This Same type or quantity production is not avaiable in Damage quantity',16,1);
	END
	
	--Insert Rate if there is no rate setup for this type 
	IF @ProcessRateID=0
	BEGIN
	INSERT INTO P_ProcessRate(ProcessID,StyleID,SizesID,ColorsID,Rate,EffectiveDate,UserID,CompanyID,ModifiedDate)
	VALUES(	@ProcessID,	@StyleID,@SizesID,@ColorsID,0,@TransactionDate,@UserID,@CompanyID,GetDate());
	
	SET @ProcessRateID=(SELECT dbo.fnGetRateID(@StyleID,@ProcessID,@SizesID,@ColorsID,@CompanyID))
	END
	
	----Finally insert or update transaction
	IF @TransactionID=0
	INSERT INTO P_Transaction 
	(EmployeeID, 
	ProcessRateID, 
	TransactionTypeID, 
	TransactionQty, 
	UnitID, 
	TransactionDate, 
	OrderID, 
	--RefID, 
	CompanyID, 
	UserID, 
	ModifiedDate)
	VALUES (
	@EmployeeID,
	(CASE WHEN @ProcessRateID>0 THEN @ProcessRateID ELSE NULL END),
	@TransactionTypeID,
	@TransactionQty,
	@UnitID,
	@TransactionDate,
	(CASE WHEN @OrderID>0 THEN @OrderID ELSE NULL END),
	--(CASE WHEN @RefID>0 THEN @RefID ELSE NULL END),
	@CompanyID,
	@UserID,
	getDate())
	
ELSE
UPDATE  P_Transaction SET 
EmployeeID = @EmployeeID, 
ProcessRateID = (CASE WHEN @ProcessRateID>0 THEN @ProcessRateID ELSE NULL END), 
TransactionTypeID = @TransactionTypeID, 
TransactionQty = @TransactionQty, 
UnitID = @UnitID, 
TransactionDate = @TransactionDate, 
OrderID = (CASE WHEN @OrderID>0 THEN @OrderID ELSE NULL END), 
--RefID = (CASE WHEN @RefID>0 THEN @RefID ELSE NULL END), 
CompanyID = @CompanyID, 
UserID = @UserID, 
ModifiedDate = getDate() 
WHERE TransactionID = @TransactionID

	
	RETURN





