CREATE PROCEDURE [dbo].[P_SpSaveOrUpdateProcessRate]
	@ProcessID int,
	@StyleID int,
	@SizesID int,
	@ColorsID int,
	@Rate money,
	@EffectiveDate dateTime,
	@UserID int,
	@CompanyID int,
	@ModifiedDate DateTime
AS
    DECLARE @Exists int,@C_SizesID int,@C_ColorsID int,@SizeID int,@ColorID int
    
    SET @EXISTS=(SELECT COUNT(*) FROM P_ProcessRate WHERE ProcessID=ProcessID AND StyleID=@StyleID)
    
    IF @EXISTS = 0
    BEGIN    
		DECLARE Sizes_CURSOR CURSOR for
			SELECT SizesID FROM P_SIZES
		DECLARE Colors_CURSOR CURSOR for
			SELECT ColorsID FROM P_COLORS
			
		OPEN Sizes_CURSOR 
		FETCH NEXT FROM Sizes_CURSOR  into @C_SizesID
		
		WHILE @@FETCH_STATUS = 0 
		BEGIN	
			OPEN Colors_CURSOR
			FETCH NEXT FROM Colors_CURSOR  into @C_ColorsID
			
			WHILE @@FETCH_STATUS = 0 
			BEGIN
				INSERT INTO P_ProcessRate(ProcessID,StyleID,SizesID,ColorsID,Rate,EffectiveDate,UserID,CompanyID,ModifiedDate)
		
				SELECT
				@ProcessID,
				@StyleID,
				@C_SizesID,
				@C_ColorsID,
				0,
				@EffectiveDate,
				@UserID,
				@CompanyID,
				@ModifiedDate
		
				FETCH NEXT FROM Colors_CURSOR  into @C_ColorsID
			END
			Close Colors_CURSOR
			FETCH NEXT FROM Sizes_CURSOR  into @C_SizesID
		END
		Close Sizes_CURSOR
	END
	
	IF @SizesID=0
		SET @SizeID=(SELECT SizesID FROM P_SIZES WHERE SizesName LIKE 'N/A')
	ELSE 
		SET @SizeID=@SizesID
		
	IF @ColorsID=0
		SET @ColorID=(SELECT ColorsID FROM P_COLORS WHERE ColorsName LIKE 'N/A')
	ELSE 
		SET @ColorID=@ColorsID
		
	UPDATE P_ProcessRate SET Rate=@Rate,EffectiveDate=@EffectiveDate,UserID=@UserID,ModifiedDate=@ModifiedDate WHERE SizesID=@SizeID AND ColorsID=@ColorID AND ProcessID=@ProcessID AND StyleID=@StyleID		
		
	RETURN





