CREATE PROCEDURE [dbo].[sp_after_requisition_report]
    @date1           Date,
@date2 date

AS
BEGIN

select party.PartyName,party.PartyCode,sum(reg.CarryingLoan) as CarrySum,dbo.func_get_total_LoanDisbursed(party.partyID,reg.CaseID) as Total_Loan_Disbursed,
dbo.func_get_after_disbursed_bags(reg.CaseID,party.PartyID) as Total_Bag_Disbursed,dbo.func_after_get_requisitioned_bags(reg.CaseID) as Requisitioned_Bags,
dbo.func_get_amounts_from_loandisbursed(reg.caseid) as After_LoanDisbursed,dbo.func_get_bags_from_loandisbursed(reg.caseid) as After_Bag,
reg.CaseID
from SRVRegistration as reg
INNER JOIN INVParty as party on reg.PartyID=party.PartyID
where reg.Requisitioned='Loan Approved' or reg.Requisitioned='Loan Disbursed' 
and reg.ModifiedDate BETWEEN @date1 and @date2
group by reg.CaseID, party.PartyName,party.PartyCode,party.partyID

END





