CREATE PROCEDURE [dbo].[SP_Party_ledger]
  @FromDate DATETIME = NULL,
  @ToDate DATETIME = NULL
   
AS
BEGIN

select CONVERT(VARCHAR(10),SIP.CreatedDate, 103) AS CreatedDate, SIP.PartyID,SIP.PartyCode,SIP.PartyName,
ISNULL(SIP.bagcount, 0) as BagLoaded,ISNULL(SBL.BagNumber, 0) as TotalBagLoan,ISNULL(SBL.BagLoan, 0) as BagLoanAmount,ISNULL(SReg.CarryingLoan, 0)as TotalCarry,
ISNULL(SLD.LoanAmount, 0) as Loan,ISNULL(ISNULL(SBL.BagLoan, 0)+ISNULL(SReg.CarryingLoan, 0)+ISNULL(SLD.LoanAmount, 0), 0) as TotalLoan
from(					
select IP.CreatedDate,IP.PartyID as PartyID ,IP.PartyName,IP.bagcount,IP.PartyCode from dbo.INVParty AS IP 
where
    (@FromDate IS NULL OR IP.CreatedDate >= @FromDate)
    AND (@ToDate IS NULL OR IP.CreatedDate <= @ToDate)
) as SIP
LEFT JOIN
(					
select BL.PartyID as PartyID, SUM(BL.BagNumber) as BagNumber,SUM(BL.LoanAmount) as BagLoan from dbo.INVParty AS IP INNER JOIN
dbo.SRVBagLoan AS BL ON BL.PartyID = IP.PartyID group by BL.PartyID) as SBL
on SIP.PartyID=SBL.PartyID
LEFT JOIN
(select Reg.PartyID,SUM(Reg.CarryingLoan) as CarryingLoan from dbo.INVParty AS IP INNER JOIN
dbo.SRVRegistration AS Reg ON IP.PartyID = Reg.PartyID group by Reg.PartyID) as SReg
on SIP.PartyID=SReg.PartyID
LEFT JOIN
(select LD.PartyID,SUM(LD.LoanAmount)as LoanAmount from dbo.INVParty AS IP INNER JOIN
                      dbo.SRVLoanDisburse AS LD ON LD.PartyID = IP.PartyID
                      group by LD.PartyID) as SLD
                      ON SIP.PartyID=SLD.PartyID
                      order by SIP.CreatedDate Desc
END




