-- exec SP_PartyWise_ledger 14,'03/03/2013','03/06/2013'
-- exec SP_Party_ledger '03/03/2013','03/06/2013'
-- =============================================

CREATE PROCEDURE [dbo].[SP_PartyWise_ledger]
  @PartyID int = NULL,
  @FromDate DATETIME = NULL,
  @ToDate DATETIME = NULL
   
AS
BEGIN

select  DR.IndividualDate,SIP.PartyID,SIP.PartyCode,SIP.PartyName,ISNULL(SIP.bagcount, 0) as BagLoaded,
		ISNULL(SBL.BagNumber, 0) as BagNumber,ISNULL(SBL.BagLoan, 0) as BagLoan,
		ISNULL(SReg.CarryingLoan , 0) as CarryingLoan,ISNULL(SLD.LoanAmount , 0) as LoanAmount,
		ISNULL(ISNULL(SBL.BagLoan, 0)+ISNULL(SReg.CarryingLoan, 0)+ISNULL(SLD.LoanAmount, 0), 0) as TotalLoan from
    ( SELECT CONVERT(VARCHAR(10),IndividualDate, 103) AS IndividualDate FROM fnDateRange('d', @FromDate,  @ToDate)) as DR
                   
LEFT JOIN
(					
select CONVERT(VARCHAR(10),IP.CreatedDate, 103) AS CreatedDate,IP.PartyID as PartyID ,IP.PartyName,IP.bagcount,IP.PartyCode from dbo.INVParty AS IP 
where IP.PartyID=@PartyID
) as SIP on DR.IndividualDate=DR.IndividualDate
LEFT JOIN
(					
select CONVERT(VARCHAR(10),BL.CreatedDate, 103) AS BagLoanDate, SUM(BL.BagNumber) as BagNumber,SUM(BL.LoanAmount) as BagLoan from dbo.SRVBagLoan AS BL 
INNER JOIN
 dbo.INVParty AS IP  ON BL.PartyID = IP.PartyID where IP.PartyID=@PartyID Group by BL.CreatedDate) as SBL
on DR.IndividualDate=SBL.BagLoanDate
LEFT JOIN
(select CONVERT(VARCHAR(10),Reg.CreatedDate, 103) AS RegDate,SUM(Reg.CarryingLoan) as CarryingLoan  from dbo.INVParty AS IP INNER JOIN
dbo.SRVRegistration AS Reg ON IP.PartyID = Reg.PartyID where IP.PartyID=@PartyID Group by  CONVERT(VARCHAR(10),Reg.CreatedDate, 103)) as SReg
on DR.IndividualDate=SReg.RegDate
LEFT JOIN
(select CONVERT(VARCHAR(10),LD.CreatedDate, 103) AS LoanDate,SUM(LD.LoanAmount) as LoanAmount from dbo.INVParty AS IP INNER JOIN
                      dbo.SRVLoanDisburse AS LD ON LD.PartyID = IP.PartyID where IP.PartyID=@PartyID Group by CONVERT(VARCHAR(10),LD.CreatedDate, 103)
                      ) as SLD
                      ON DR.IndividualDate=SLD.LoanDate
                      where 
                      SBL.BagNumber IS NOT NULL  OR
                      SBL.BagLoan IS NOT NULL  OR
                      SReg.CarryingLoan IS NOT NULL OR
                      SLD.LoanAmount IS NOT NULL 
                      order by DR.IndividualDate Asc
END




