CREATE PROCEDURE [dbo].[spAttendanceDownLoad]
	@startDate   DateTime,
	@endDate DateTime,
	@CompanyID int,
	@userID int,
	@hasCardno int,
	@hasProximity int
AS

--DELETE FROM AttendanceDownload WHERE AttDate BETWEEN @startDate AND @endDate AND CompanyID=@CompanyID	

IF @hasCardno=1	
BEGIN
INSERT INTO AttendanceDownload(EmployeeID, AttTime,AttDate, ScheduleID,shiftID, SerialNo,CompanyID,UserID,ModifiedDate)

SELECT  E.EmployeeID, 
		A.ATT_DATE + A.ATT_TIME AS Att_Time, 
		(CASE WHEN Att_Time BETWEEN E.DayMid AND E.DayEnd THEN A.Att_Date - 1 ELSE A.Att_Date END) AS AttDate, 
		E.ScheduleID, 
		E.ShiftID, 
		A.SERIAL_NO, 
		@CompanyID AS companyID, 
        @UserID AS userID, 
        GETDATE() AS modifieddate
FROM  (SELECT Employee.EmployeeID, Employee.CardNo, EmployeeShift.ScheduleID, EmployeeShift.ShiftID, EmployeeShift.effectiveFrom,EmployeeShift.effectiveTo,Schedule.DayStart,Schedule.DayMid,Schedule.DayEnd,Employee.CompanyID FROM Schedule INNER JOIN EmployeeShift ON Schedule.ScheduleID = EmployeeShift.ScheduleID INNER JOIN Employee ON EmployeeShift.EmployeeID = Employee.EmployeeID) E
		INNER JOIN RTA.dbo.RTA_DOWNLOADED AS A 
ON ( (E.CardNo = substring(A.CARDNO,3,50) OR E.CardNo = A.CARDNO ) AND (A.att_date between E.effectivefrom and E.effectiveto) )
WHERE (A.ATT_DATE = @startDate ) AND (A.DOWNLOAD_STATUS = 0) AND (E.CompanyID = @CompanyID)
	
END

IF @hasProximity=1	
BEGIN
INSERT INTO AttendanceDownload(EmployeeID, AttTime,AttDate, ScheduleID,shiftID, SerialNo,CompanyID,UserID,ModifiedDate)

SELECT  E.EmployeeID, 
		A.ATT_DATE + A.ATT_TIME AS Att_Time, 
		(CASE WHEN Att_Time BETWEEN E.DayMid AND E.DayEnd THEN A.Att_Date - 1 ELSE A.Att_Date END) AS AttDate, 
		E.ScheduleID, 
		E.ShiftID, 
		A.SERIAL_NO, 
		@CompanyID AS companyID, 
        @UserID AS userID, 
        GETDATE() AS modifieddate
FROM  (SELECT Employee.EmployeeID,Employee.ProximityNo, EmployeeShift.ScheduleID, EmployeeShift.ShiftID, EmployeeShift.effectiveFrom,EmployeeShift.effectiveTo,Schedule.DayStart,Schedule.DayMid,Schedule.DayEnd,Employee.CompanyID FROM Schedule INNER JOIN EmployeeShift ON Schedule.ScheduleID = EmployeeShift.ScheduleID INNER JOIN Employee ON EmployeeShift.EmployeeID = Employee.EmployeeID) E
		INNER JOIN RTA.dbo.RTA_DOWNLOADED AS A 
ON ( E.ProximityNo = A.CARDNO  AND (A.att_date between E.effectivefrom and E.effectiveto) )
WHERE (A.ATT_DATE = @startDate ) AND (A.DOWNLOAD_STATUS = 0) AND (E.CompanyID = @CompanyID)
	
END
	
RETURN









