CREATE PROCEDURE [dbo].[spDeleteExportBill]
	@Step Varchar(100),
	@BillID INT
    
AS
BEGIN

Declare @RTID INT,@TID INT
Declare @RTIDs Varchar(1000)
IF @Step LIKE 'R' --Realization step
BEGIN

	SET @RTIDs= ISNULL((SELECT RealizedTransRefIDs FROM T_Export_Bill WHERE BillID=@BillID),'');
	DELETE FROM  T_Bill_Accounts WHERE BillID = @BillID AND ref NOT LIKE 'First Voucher'

	UPDATE T_Export_Bill SET  RealisedAmt=0, RealisedLoss=0, RealisedDate=NULL,RealizedTransRefID=NULL,RealizedTransRefIDs=''
	WHERE BillID=@BillID
	
	
Declare @cur Cursor ;
Declare @OneID INT
SET @cur = CURSOR FOR SELECT DISTINCT ID FROM dbo.fnSelectedIDList(@RTIDs);

OPEN @cur
FETCH NEXT FROM @cur INTO @OneID 

WHILE (@@FETCH_STATUS = 0)
BEGIN
IF ISNULL(@OneID,0) > 0
EXEC spDeleteTransaction @OneID;
FETCH NEXT FROM @cur INTO @OneID 
END
CLOSE @cur
END
ELSE IF @Step LIKE 'BP' --Bill Purchase step
BEGIN
	SET @RTID= (SELECT RealizedTransRefID FROM T_Export_Bill WHERE BillID=@BillID);
	SET @TID= (SELECT TransRefID FROM T_Export_Bill WHERE BillID=@BillID);

	DELETE FROM  T_Bill_Accounts WHERE BillID = @BillID ;
	UPDATE T_Export_Bill SET LoanNo=NULL,AcceptDate=NULL, MaturityDate=NULL, TransRefID=NULL,PurchaseDate=NULL, PurchaseAmount=0, RealisedAmt=0, RealisedLoss=0, RealisedDate=NULL,RealizedTransRefID=NULL
	WHERE BillID=@BillID

	IF ISNULL(@RTID,0) > 0
	EXEC spDeleteTransaction @RTID;
	IF ISNULL(@TID,0) > 0
	EXEC spDeleteTransaction @TID;
END
ELSE IF @Step LIKE 'BE'  --Bill Entry step
BEGIN
	SET @RTID= (SELECT RealizedTransRefID FROM T_Export_Bill WHERE BillID=@BillID);
	SET @TID= (SELECT TransRefID FROM T_Export_Bill WHERE BillID=@BillID);

	DELETE FROM  T_Bill_Accounts WHERE BillID = @BillID ;
	DELETE FROM T_ExportBill_Invoices WHERE BillID = @BillID ;
	DELETE FROM T_Export_Bill WHERE BillID=@BillID

	IF ISNULL(@RTID,0) > 0
		EXEC spDeleteTransaction @RTID;
		IF ISNULL(@TID,0) > 0
		EXEC spDeleteTransaction @TID;

END
END






GO

