CREATE PROCEDURE [dbo].[spDeleteTransAccount]
	@TransDID INT
AS
	/* SET NOCOUNT ON */
	
	BEGIN
	Declare @PrevAmt Money;
	Declare @AccountID INT;
	SET @PrevAmt = (SELECT ISNULL(DebitAmt,0) - ISNULL(CreditAmt,0) FROM T_Transaction_Detail WHERE TransDID = @TransDID)
	SET @AccountID = (SELECT AccountID FROM T_Transaction_Detail WHERE TransDID = @TransDID)
	
	DELETE FROM T_Transaction_Detail WHERE TransDID = @TransDID;
	
	UPDATE T_Account SET CurrentBalance=CurrentBalance - Nature*@PrevAmt  WHERE AccountID=@AccountID;
	END
	RETURN






