CREATE PROCEDURE [dbo].[spDeleteTransaction]
	@TransMID INT
AS
	/* SET NOCOUNT ON */
	
	BEGIN
	
	
	Declare @TransDID INT;
	Declare @curTransDtl Cursor ;
	SET @curTransDtl = CURSOR FOR SELECT TransDID FROM T_Transaction_Detail WHERE TransMID = @TransMID;
	
	OPEN @curTransDtl;
	
	FETCH NEXT FROM @curTransDtl INTO @TransDID;
	
	WHILE (@@FETCH_STATUS = 0)
	BEGIN
	EXEC spDeleteTransAccount @TransDID ;
	FETCH NEXT FROM @curTransDtl INTO @TransDID;
	END
	
	CLOSE @curTransDtl;
	DEALLOCATE @curTransDtl;
	
	DELETE FROM T_Transaction_Master WHERE TransMID = @TransMID
	
	END
	RETURN





