CREATE PROCEDURE [dbo].[spEarnLeaveReport]
@CardNo varchar(50),
@DesignationID int,
@SectionID int,
@FloorID int,
@LineID int,
@CompanyID int,
@FiscalYearID int
AS
SELECT     vw_Employees.EmployeeID, vw_Employees.CardNo, vw_Employees.Name, vw_Employees.DesignationID, vw_Employees.DesignationName, 
                      vw_Employees.SectionID, vw_Employees.SectionName, vw_Employees.FloorID, vw_Employees.FloorName, vw_Employees.LineID, 
                      vw_Employees.LineName, ISNULL(EmployeeEarnLeave.month1, 0) + ISNULL(EmployeeEarnLeave.month2, 0) + ISNULL(EmployeeEarnLeave.month3, 
                      0) + ISNULL(EmployeeEarnLeave.month4, 0) + ISNULL(EmployeeEarnLeave.month5, 0) + ISNULL(EmployeeEarnLeave.month6, 0) 
                      + ISNULL(EmployeeEarnLeave.month7, 0) + ISNULL(EmployeeEarnLeave.month8, 0) + ISNULL(EmployeeEarnLeave.month9, 0) 
                      + ISNULL(EmployeeEarnLeave.month10, 0) + ISNULL(EmployeeEarnLeave.month11, 0) + ISNULL(EmployeeEarnLeave.month12, 0) AS PresentDays, 
                      (ISNULL(EmployeeEarnLeave.month1, 0) + ISNULL(EmployeeEarnLeave.month2, 0) + ISNULL(EmployeeEarnLeave.month3, 0) 
                      + ISNULL(EmployeeEarnLeave.month4, 0) + ISNULL(EmployeeEarnLeave.month5, 0) + ISNULL(EmployeeEarnLeave.month6, 0) 
                      + ISNULL(EmployeeEarnLeave.month7, 0) + ISNULL(EmployeeEarnLeave.month8, 0) + ISNULL(EmployeeEarnLeave.month9, 0) 
                      + ISNULL(EmployeeEarnLeave.month10, 0) + ISNULL(EmployeeEarnLeave.month11, 0) + ISNULL(EmployeeEarnLeave.month12, 0)) 
                      / 18.0 AS LeaveDays, EmployeeEarnLeave.OpeningBalance, EmployeeEarnLeave.earnLeave, 
                      EmployeeEarnLeave.earnLeave - EmployeeEarnLeave.LeaveTransaction AS CurrentBalance, dbo.OneDay_Gross(vw_Employees.EmployeeID,
                          (SELECT     TOP (1) startdate
                            FROM          FiscalYear
                            WHERE      (FiscalYearID = EmployeeEarnLeave.FiscalYearID)),
                          (SELECT     TOP (1) enddate
                            FROM          FiscalYear AS FiscalYear_1
                            WHERE      (FiscalYearID = EmployeeEarnLeave.FiscalYearID)), vw_Employees.CompanyID) 
                      * (EmployeeEarnLeave.earnLeave - EmployeeEarnLeave.LeaveTransaction) AS EarnLeaveAmount, EmployeeEarnLeave.FiscalYearID, 
                      EmployeeEarnLeave.LeaveTransaction, FiscalYear_2.Title
FROM         vw_Employees INNER JOIN
                      EmployeeEarnLeave ON vw_Employees.EmployeeID = EmployeeEarnLeave.EmployeeID INNER JOIN
                      FiscalYear AS FiscalYear_2 ON EmployeeEarnLeave.FiscalYearID = FiscalYear_2.FiscalYearID where
                      (vw_Employees.CardNo = @CardNo OR
                      @CardNo = 'all') AND (vw_Employees.SectionID = @SectionID OR
                      @SectionID = 0) AND (vw_Employees.FloorID = @FloorID OR
                      @FloorID = 0) AND (vw_Employees.LineID = @LineID OR
                      @LineID = 0) AND (vw_Employees.CompanyID = @CompanyID) AND (vw_Employees.DesignationID = @DesignationID OR
                      @DesignationID = 0) AND  ( EmployeeEarnLeave.FiscalYearID=@FiscalYearID OR @FiscalYearID=0)
                      
	RETURN





