CREATE PROCEDURE [dbo].[spEmployeeJobCalendar]
	@EmployeeName Varchar(100),
	@CardNo Varchar(100),
	@EmpCategoryID	int,
	@DesignationID	int,
    @SectionID	int,
    @FloorID	int,
    @LineID	int,
    @CompanyID int
    
AS

	Declare @QueryStr Varchar(2000)
	SET @QueryStr = 'SELECT     vw_Employees.Name as eName, vw_Employees.EmployeeID as eID, vw_Employees.CardNo as eCardNo, JobCalender.Name AS JobCalenderName
FROM         vw_Employees INNER JOIN
                      EmployeeJobCalendar ON vw_Employees.EmployeeID = EmployeeJobCalendar.EmployeeID INNER JOIN
                      JobCalender ON EmployeeJobCalendar.JobCalenderID = JobCalender.JobCalenderID WHERE (1=1)'
	
	/* Employee Name*/
	IF @EmployeeName <> 'all'
	  SET @QueryStr = @QueryStr + ' AND vw_Employees.Name Like '+char(39) +@EmployeeName +'%' +char(39)
		
