		/* Designation */
	IF @EmpCategoryID <> 0
	  SET @QueryStr = @QueryStr + ' AND vw_Employees.EmpCategoryID = '+ str(@EmpCategoryID) 
	  
		/* Designation */
	IF @DesignationID <> 0
	  SET @QueryStr = @QueryStr + ' AND vw_Employees.DesignationID = '+ str(@DesignationID) 
	  
	  /* Section*/
	IF @SectionID <> 0
	  SET @QueryStr = @QueryStr + ' AND vw_Employees.SectionID = '+ str(@SectionID )
	  
	  /* Floor*/
