	  SET @QueryStr = @QueryStr + ' AND vw_Employees.FloorID = '+ str(@FloorID) 
	 
	  /* Line*/
	IF @LineID <> 0
	  SET @QueryStr = @QueryStr + ' AND vw_Employees.LineID = '+ str(@LineID) 
	     
	 set @Querystr =@Querystr + ' AND EmployeeJobCalendar.CompanyID = '+str(@CompanyID)+'  ORDER BY eCardNo,eName'    
