CREATE PROCEDURE [dbo].[spEmployeeSalaryUpdate]
@SectionID int,
@FloorID int,
@LineID int,
@DesignationID INT,
@CardNo varchar(50),
@CompanyID int,
@UserID int,
@ModifiedDate datetime
AS
set xact_abort on;
begin tran
DECLARE @EmployeeID int, 
 
		@PayScaleID int, 
		@Salary money,
		@CalculationRuleID  INT,
		@BasicSalary money,
		@GrossSalary money,
		@HouseRent money,
		@Medical money,
		@Conveyance money,
		
		@HouseRentRate float,
		@HouseRentFixed money,
		@MedicalRate float,
		@MedicalFixed money,
		@SalaryRate float ,
		@ConveyanceFixed money,
		@ConveyanceRate float
		
DECLARE ES_CURSOR CURSOR for SELECT     EmployeeID, PayScaleID, Salary
                             FROM  vw_Employees WHERE CompanyID = @CompanyID
                                                AND (SectionID=@SectionID OR @SectionID=0)
                                                AND (DesignationID=@DesignationID OR @DesignationID=0)
                                                AND (FloorID=@FloorID OR @FloorID=0)
                                                AND (LineID=@LineID OR @LineID=0)
                                                AND (CardNo=@Cardno OR @CardNo='all')
DELETE FROM EmployeeSalary WHERE  CompanyID = @CompanyID AND EmployeeID  in (Select Employee.EmployeeID FROM Employee  INNER JOIN EmployeeHistory
	                                  ON Employee.EmployeeHistoryID = EmployeeHistory.EmployeeHistoryID  WHERE (SectionID=@SectionID OR @SectionID=0) AND (DesignationID=@DesignationID OR @DesignationID=0) AND (FloorID=@FloorID OR @FloorID=0) AND (lineID=@lineID OR @lineID=0) AND (Cardno=@Cardno OR @Cardno='all'));

OPEN ES_CURSOR 
FETCH NEXT FROM ES_CURSOR  into @EmployeeID,  @PayScaleID, @Salary
WHILE @@FETCH_STATUS = 0 
	BEGIN
	SET  @CalculationRuleID  =(SELECT     CalculationRuleID 
								FROM         SalaryBreakDown
								WHERE     (PayScaleID = @PayScaleID) AND CompanyID = @CompanyID
							  )
		SET  @HouseRentRate  =(SELECT     HouseRentRate 
								FROM         SalaryBreakDown
								WHERE     (PayScaleID = @PayScaleID) AND CompanyID = @CompanyID
							  )
	SET  @HouseRentFixed  =(SELECT     HouseRentFixed 
								FROM         SalaryBreakDown
								WHERE     (PayScaleID = @PayScaleID) AND CompanyID = @CompanyID
							  )
	SET  @MedicalRate  =(SELECT     MedicalRate 
								FROM         SalaryBreakDown
								WHERE     (PayScaleID = @PayScaleID) AND CompanyID = @CompanyID
							  )
	SET  @MedicalFixed  =(SELECT     MedicalFixed 
								FROM         SalaryBreakDown
								WHERE     (PayScaleID = @PayScaleID) AND CompanyID = @CompanyID
							  )
	SET  @SalaryRate  =(SELECT     SalaryRate 
								FROM         SalaryBreakDown
								WHERE     (PayScaleID = @PayScaleID) AND CompanyID = @CompanyID
							  )
	SET  @ConveyanceFixed  =(SELECT     ConveyanceFixed 
								FROM         SalaryBreakDown
								WHERE     (PayScaleID = @PayScaleID) AND CompanyID = @CompanyID
							  )
	SET  @ConveyanceRate  =(SELECT     ConveyanceRate 
								FROM         SalaryBreakDown
								WHERE     (PayScaleID = @PayScaleID) AND CompanyID = @CompanyID
							  )
	IF @Salary=NULL
		SET @Salary=0
	IF @CalculationRuleID = 21 ---Input Gross Calculated By Gross
	BEGIN
		SET @GrossSalary = @Salary
		SET @BasicSalary = @SalaryRate*@GrossSalary/100
		SET @HouseRent = @HouseRentFixed + @HouseRentRate*@GrossSalary/100
		SET @Medical = @MedicalFixed + @MedicalRate*@GrossSalary/100
		SET @Conveyance = @ConveyanceFixed + @ConveyanceRate*@GrossSalary/100
		
	END
	ELSE IF @CalculationRuleID = 22 ---------Input Gross Calculated By Basic
	BEGIN
		SET @GrossSalary = @Salary
		SET @BasicSalary = (@GrossSalary-@HouseRentFixed-@MedicalFixed-@ConveyanceFixed)/(1+@HouseRentRate/100+@MedicalRate/100+@ConveyanceRate/100)
		SET @HouseRent = @HouseRentFixed + @HouseRentRate*@BasicSalary/100
		SET @Medical = @MedicalFixed + @MedicalRate*@BasicSalary/100
		SET @Conveyance = @ConveyanceFixed + @ConveyanceRate*@BasicSalary/100
		
	END
	ELSE IF @CalculationRuleID = 23 ---------------Input Basic Calculated By Basic
	BEGIN
		SET @BasicSalary= @Salary
		SET @GrossSalary = @BasicSalary+@HouseRentFixed+@MedicalFixed+@ConveyanceFixed+@BasicSalary*(@HouseRentRate/100+@MedicalRate/100+@ConveyanceRate/100)
		SET @HouseRent = @HouseRentFixed + @HouseRentRate*@BasicSalary/100
		SET @Medical = @MedicalFixed + @MedicalRate*@BasicSalary/100
		SET @Conveyance = @ConveyanceFixed + @ConveyanceRate*@BasicSalary/100
		
	END
	
		INSERT INTO EmployeeSalary (EmployeeID,BasicSalary,GrossSalary,HouseRent,Medical,Conveyance,CompanyID,UserID,ModifiedDate)
		VALUES (@EmployeeID,@BasicSalary,@GrossSalary,@HouseRent,@Medical,@Conveyance,@CompanyID,@UserID,@ModifiedDate)
	
	
	FETCH NEXT FROM ES_CURSOR into @EmployeeID, @PayScaleID, @Salary
	END
Close ES_CURSOR
DEALLOCATE ES_CURSOR
commit;
	RETURN





GO

