CREATE PROCEDURE [dbo].[spGeneralManpowerSubReport]
	@attDate DateTime,
	@DesignationID int,
	@SectionID int,
	@FloorID int,
	@LineID int,
	@CompanyID int
AS
declare @present int,
@Absent int,
@total int,
@perPresent money,
@perAbsent money
	/* SET NOCOUNT ON */
	
set @total=(SELECT  
        COUNT(ISNULL(A.type, 0))

FROM    (SELECT EmployeeID,type,CompanyID FROM vw_AttInOut WHERE (vw_AttInOut.attDate = @attDate ) AND (vw_AttInOut.CompanyID = @CompanyID) ) A 
right outer JOIN
        (SELECT EmployeeID,CardNo,Gender,DesignationName,SectionName,FloorName,LineName,CompanyID FROM vw_Employees WHERE  (vw_Employees.DesignationID = @DesignationID OR @DesignationID=0) AND 
	   (vw_Employees.SectionID = @SectionID OR @SectionID=0) AND 
	   (vw_Employees.FloorID = @FloorID OR @FloorID=0) AND 
       (vw_Employees.LineID = @LineID OR @LineID=0) AND (vw_Employees.CompanyID = @CompanyID)) B
        ON A.EmployeeID = B.EmployeeID)
set @Absent=(SELECT  
        COUNT(ISNULL(A.type, 0))

FROM    (SELECT EmployeeID,type,CompanyID FROM vw_AttInOut WHERE (vw_AttInOut.attDate = @attDate ) AND (vw_AttInOut.CompanyID = @CompanyID) ) A 
right outer JOIN
        (SELECT EmployeeID,CardNo,Gender,DesignationName,SectionName,FloorName,LineName,CompanyID FROM vw_Employees WHERE  (vw_Employees.DesignationID = @DesignationID OR @DesignationID=0) AND 
	   (vw_Employees.SectionID = @SectionID OR @SectionID=0) AND 
	   (vw_Employees.FloorID = @FloorID OR @FloorID=0) AND 
       (vw_Employees.LineID = @LineID OR @LineID=0) AND (vw_Employees.CompanyID = @CompanyID)) B
        ON A.EmployeeID = B.EmployeeID where A.type is null or A.type='Leave' or A.type='Holiday' )
  set @present=@total-@Absent 
  if @present+@Absent <> 0 
  begin
  set @perPresent=@present*100.0/(@present+@Absent)
  set @perAbsent=@Absent*100.0/(@present+@Absent)
  end
  else
  begin 
    set @perPresent=0
  set @perAbsent=0
  end
   
      select @perPresent  as PerPresent, @perAbsent as PerAbsent
   
       
	RETURN







GO

