CREATE PROCEDURE [dbo].[spGenerateCustomSalarySheet]
    @startDate DATETIME,
	@endDate DATETIME,
	@CompanyID INT,
	@SalarySheetID INT,
	@filterStr NVARCHAR(500)
AS
	 SET NOCOUNT ON 
	DECLARE @fn NVARCHAR(4000);
	DECLARE @qstr NVARCHAR(4000);
	Declare @delQstr NVARCHAR(1000);
	DECLARE @exeFunction CURSOR ;
	
	set xact_abort on;
	 begin tran
	set @delQstr= 'DELETE FROM CustomeSalarySheet WHERE SalarySheetID=@SalarySheetID AND salMonth=MONTH(@startDate) AND salYear=YEAR(@startDate) AND CompanyID=@CompanyID 
	                                  AND EmployeeID  in (Select Employee.EmployeeID FROM Employee  INNER JOIN EmployeeHistory
	                                  ON Employee.EmployeeHistoryID = EmployeeHistory.EmployeeHistoryID '+ @filterStr + ')';
	exec sp_executeSQL @delQstr,N'@SalarySheetID INT,@startDate DateTime,@CompanyID INT',@SalarySheetID,@startDate,@CompanyID ;
	
	SET @exeFunction =CURSOR FOR select Fns  FROM dbo.vw_SalarySheetExecutablefunctions WHERE SalarySheetID=@SalarySheetID AND CompanyID=@CompanyID;
	open @exeFunction;
	Fetch Next From @exeFunction into @fn

	
	 While(@@FETCH_STATUS = 0)
     Begin
     set @qstr= @fn + ' ' + @filterStr ;
  
    
     exec sp_executeSQL @qstr,N'@startDate DateTime,@endDate DateTime,@CompanyID INT',@startDate,@endDate,@CompanyID;
    
     Fetch Next From @exeFunction into @fn
     
     end
     
     Close @exeFunction
Deallocate @exeFunction


commit
	
RETURN
	
	






