INSERT INTO CustomeSalarySheet  (EmployeeID, SalarySheetID, SDate, EDate, SalMonth, SalYear, Particulars, SValue, PaySlipAdd, Active, CompanyID, PayDeduct, functionname,DisplayOrder,forSummary)
SELECT  VW_Employees.EmployeeID, 
