CREATE PROCEDURE [dbo].[spInsertOrUpdateAccount]
	
	@AccountID INT,
	@AccountNo Varchar(100),
	@AccountTitle nVarchar(500),
	@AccOrGroup Varchar(50),
	@AccDepth INT,
	@Nature INT,
	@AccountStatus Varchar(100),
	@AccCreateDate DateTime,
	@ParentID INT,
	@OpeningBalance Money,
	@IsInventoryRelated INT,
	@LedgerTypeID Int,
	@LedgerID INT
     
As

Declare @CompanyID int = 1;
Declare	@UserID int =1;
IF @AccountID <=0 
INSERT INTO T_Account
       (AccountNo, AccountTitle, AccOrGroup, AccDepth, Nature, AccountStatus, AccCreateDate, ParentID, OpeningBalance,CurrentBalance, IsInventoryRelated,LedgerTypeID, LedgerID, 
       CompanyID, UserID, ModifiedDate)
VALUES (@AccountNo,@AccountTitle,@AccOrGroup,@AccDepth,@Nature,@AccountStatus,@AccCreateDate,@ParentID,@OpeningBalance,@OpeningBalance,@IsInventoryRelated,@LedgerTypeID,NULL,@CompanyID,@UserID,
       GETDATE())
                       
ELSE
BEGIN
DeClare @prevBal Money;
SET @prevBal = (SELECT ISNULL(OpeningBalance,0) FROM T_Account WHERE AccountID=@AccountID);
UPDATE  T_Account
SET  AccountNo = @AccountNo, AccountTitle = @AccountTitle, AccOrGroup = @AccOrGroup, AccDepth = @AccDepth, Nature = @Nature, 
     AccountStatus = @AccountStatus, AccCreateDate = @AccCreateDate, ParentID = @ParentID, OpeningBalance = @OpeningBalance, 
     CurrentBalance = (CurrentBalance - @prevBal + @OpeningBalance),
     IsInventoryRelated = @IsInventoryRelated,LedgerTypeID=@LedgerTypeID, LedgerID = NULL, CompanyID = @CompanyID, UserID = @UserID, ModifiedDate = GETDATE()
WHERE (AccountID = @AccountID)
END
IF @AccountID <= 0
SET @AccountID=(SELECT MAX(AccountID) FROM T_Account);


IF 	@AccDepth > 2 AND @AccOrGroup LIKE 'Group' 
UPDATE T_Account SET FinalParentID=(SELECT FinalParentID FROM T_Account WHERE AccountID=@ParentID) WHERE AccountID=@AccountID;
ELSE
UPDATE T_Account SET FinalParentID=ParentID WHERE AccountID=@AccountID;
	RETURN  SCOPE_IDENTITY()






GO

