CREATE PROCEDURE [dbo].[spInsertUpdateTransDetail]
	@TransDID INT,
	@TransMID INT,
	@AccountID INT,
	@CreditAmt Money,
	@DebitAmt Money,
	@Comments Varchar(500)
AS
	/* SET NOCOUNT ON */
	
	
	IF @TransDID <= 0
	BEGIN
	
	INSERT INTO T_Transaction_Detail
                      (TransMID, AccountID, CreditAmt, DebitAmt, Comments)
	VALUES     (@TransMID,@AccountID,@CreditAmt,@DebitAmt,@Comments)
	
	--UPDATE T_Account SET CurrentBalance=CurrentBalance + @DebitAmt - @CreditAmt WHERE AccountID=@AccountID;
	UPDATE T_Account SET CurrentBalance=CurrentBalance + Nature*(@DebitAmt - @CreditAmt) WHERE AccountID=@AccountID;
	END
	ELSE
	BEGIN
	Declare @PrevAmt Money;
	SET @PrevAmt = (SELECT ISNULL(DebitAmt,0) - ISNULL(CreditAmt,0) FROM T_Transaction_Detail WHERE TransDID = @TransDID)
	
	UPDATE    T_Transaction_Detail
	SET       TransMID = @TransMID, AccountID = @AccountID, CreditAmt = @CreditAmt, DebitAmt = @DebitAmt, Comments = @Comments
	WHERE     (TransDID = @TransDID)
