CREATE PROCEDURE [dbo].[spLoadLoanWithInterest]
@LoanID INT,
@PayDate DateTime
AS
BEGIN
DECLARE @LastDate DateTime, @Period Varchar(100),@Date INT;
SET @LastDate =ISNULL((SELECT MAX(AdjustDate) FROM T_LoanAdjust WHERE LoanID = @LoanID), (SELECT SanktionDate FROM T_Loan WHERE LoanID = @LoanID));
SET @Period =ISNULL((SELECT MAX(InterestPeriod) FROM T_Loan WHERE LoanID = @LoanID), '')
IF @Period = 'Monthly'
SET @Date = 30;
ELSE
SET @Date = 365;

SELECT     dbo.T_Loan.*,
( DateDiff(day, @LastDate, @PayDate) * dbo.T_Loan.InterestRate * dbo.T_Loan.DueAmount) /(100.0*@Date) AS Interest
FROM         dbo.T_Loan 
WHERE     (dbo.T_Loan.LoanID = @LoanID)
END









