CREATE PROCEDURE [dbo].[spManHours]
	@startDate DateTime,
	@endDate DateTime,
	@SectionID INT,
	@CompanyID INT
AS
	/* SET NOCOUNT ON */
	
Declare @MaxOT as INT


SET @MaxOT = isnull((SELECT top 1 MaxOverTime FROM SalarySheets WHERE IsDefault = 1 AND CompanyID = @CompanyID),0)
--select @MaxOT;

IF @MaxOT >0 --for buyer
SELECT attDate, SectionName,COUNT(vw_Attendance.employeeID) AS manpower,SUM(DateDiff(hour,StartTime, EndTime)) AS NormalHour, StartTime, EndTime, SUM(CASE WHEN OT > @MaxOT*60 THEN @MaxOT ELSE OT/60.0 END) AS OT, SUM(DATEDIFF(hour, StartTime, EndTime)) AS Hour
FROM   vw_Attendance INNER JOIN VW_Employees ON vw_Attendance.employeeID=VW_Employees.employeeID
WHERE     (attDate BETWEEN @startDate AND @endDate) AND (vw_Attendance.CompanyID=@CompanyID) AND (SectionID=@SectionID OR @SectionID=0) AND
(attDate NOT IN (SELECT holidaydate from Holidays WHERE (holidaydate BETWEEN @startDate AND @endDate) AND CompanyID=@CompanyID))
GROUP BY attDate,SectionName, StartTime, EndTime
ELSE

SELECT attDate,SectionName, COUNT(vw_Attendance.employeeID) AS manpower, SUM(DateDiff(hour,StartTime, EndTime)) AS NormalHour,StartTime, EndTime, SUM(OT/60.0) AS OT, SUM(DATEDIFF(hour, StartTime, EndTime)) AS Hour
FROM   vw_Attendance INNER JOIN VW_Employees ON vw_Attendance.employeeID=VW_Employees.employeeID
WHERE     (attDate BETWEEN @startDate AND @endDate) AND (vw_Attendance.CompanyID=@CompanyID) AND(SectionID=@SectionID OR @SectionID=0)
GROUP BY attDate,SectionName, StartTime, EndTime

	RETURN





GO

