set @H='H'
set @A='A'
EXEC spGenerateDummyDate @startDate,@endDate

 
SELECT  E.CardNo, E.Name, E.SectionName,ATT.Type,ATT.value FROM  vw_Employees  E INNER JOIN 

(
SELECT  employeeID, count(attDate) as value, @attDay AS Type FROM vw_AttInOut WHERE attDate BETWEEN @startDate AND @endDate GROUP BY employeeID
UNION ALL
SELECT  employeeID, count(attDate) as value, @p AS Type FROM vw_Attendance WHERE attDate BETWEEN @startDate AND @endDate GROUP BY employeeID
UNION ALL
