UNION ALL
SELECT  EmployeeID, COUNT(LatePresentDate) as value, @L AS Type FROM LatePresentDays  WHERE LatePresentDate BETWEEN @startDate AND @endDate GROUP BY employeeID
UNION ALL
SELECT  EmployeeID, SUM(late_min)/60.0 as value, @LHr AS Type FROM LatePresentDays WHERE late_min >0  AND LatePresentDate BETWEEN @startDate AND @endDate GROUP BY employeeID
UNION ALL
SELECT  EmployeeID, COUNT(HoliDayDate) as value, @H AS Type FROM HoliDays WHERE HoliDayDate BETWEEN @startDate AND @endDate GROUP BY employeeID
UNION ALL
SELECT  EmployeeID, COUNT(LeaveDate) as value ,SUBSTRING(LeaveType.LeaveType,1,1) as Type FROM LeaveDays INNER JOIN LeaveType ON SUBSTRING(Remarks,1,CHARINDEX(':',Remarks)-1)like LeaveType.LeaveTypeID WHERE LeaveDate BETWEEN @startDate AND @endDate GROUP BY employeeID,SUBSTRING(LeaveType.LeaveType,1,1)
UNION ALL
SELECT EmployeeID, COUNT(AbsentDate) AS value, @A AS Type FROM vw_AbsentDays WHERE AbsentDate BETWEEN @startDate AND @endDate GROUP BY EmployeeID  


