UNION ALL
SELECT  null, 0 as value, 'L' AS Type 
UNION ALL
SELECT  null, 0 as value, 'LHr' AS Type 
UNION ALL
SELECT  null, 0 as value, 'H' AS Type 
UNION ALL
SELECT  1, 0 as value, SUBSTRING(ERP.dbo.LeaveType.LeaveType,1,1) AS Type FROM ERP.dbo.LeaveType*/
) ATT

ON   E.employeeID=ATT.EmployeeID 
