CREATE PROCEDURE [dbo].[spOrderSheet]
	@OrderMID INT,
	@orderType varchar(100),
	@CompanyID int
AS
/*
SELECT     Order_Details.ItemID, T_Ledgers.TeamID, T_Ledgers.LedgerName, Factory.FactoryName, Order_Master.OrderNo, Order_Master.OrderDate, 
                      Order_Master.DeliveryDate, Order_Master.LedgerNo, T_Team_Master.TeamNo, VW_Items.ItemName, VW_Items.ColorsName, VW_Items.ShadeNo, 
                      VW_Items.CountName, VW_Items.UnitsName, Order_Details.OrderQty, Order_Details.UnitPrice, Order_Details.OrderValue, VW_Items.ItemDescription, 
                      Order_Master.OrderType, Order_Master.CompanyID, VW_Items.HSCode, Order_Master.OrderMID, VW_Items.SizesName
FROM         T_Ledgers INNER JOIN
                      Order_Details INNER JOIN
                      Order_Master ON Order_Details.OrderMID = Order_Master.OrderMID INNER JOIN
                      VW_Items ON Order_Details.ItemID = VW_Items.ItemID ON T_Ledgers.LedgerID = Order_Master.CustomerID LEFT OUTER JOIN
                      T_Team_Detail INNER JOIN
                      T_Team_Master ON T_Team_Detail.TeamID = T_Team_Master.TeamID ON T_Ledgers.TeamID = T_Team_Master.TeamID LEFT OUTER JOIN
                      Factory ON T_Ledgers.LedgerID = Factory.CustomerID AND Order_Master.FactoryID = Factory.FactoryID	
Where Order_Master.OrderType=@orderType and (Order_Master.OrderMID =@OrderMID)And Order_Master.CompanyID=@CompanyID */

SELECT     Order_Details.ItemID, T_Ledgers.TeamID, T_Ledgers.LedgerName, Factory.FactoryName, Order_Master.OrderNo, Order_Master.OrderDate, 
                      Order_Master.DeliveryDate, Order_Master.LedgerNo, T_Team_Master.TeamNo, Order_Details.OrderQty, Order_Details.UnitPrice, 
                      Order_Details.OrderValue, Order_Master.OrderType, Order_Master.CompanyID, Order_Master.OrderMID, T_Item.HSCode, T_Item.ItemName, 
                      T_Item.ItemDescription, T_Count.CountName, P_Sizes.SizesName, P_Colors.ColorsName, P_Units.UnitsName, Order_Details.ColorCode, 
                      Order_Details.Labdip AS ShadeNo
FROM         T_Ledgers INNER JOIN
                      Order_Details INNER JOIN
                      Order_Master ON Order_Details.OrderMID = Order_Master.OrderMID ON T_Ledgers.LedgerID = Order_Master.CustomerID INNER JOIN
                      T_Item ON Order_Details.ItemID = T_Item.ItemID INNER JOIN
                      P_Units ON T_Item.UnitID = P_Units.UnitsID LEFT OUTER JOIN
                      T_Team_Detail INNER JOIN
                      T_Team_Master ON T_Team_Detail.TeamID = T_Team_Master.TeamID ON T_Ledgers.TeamID = T_Team_Detail.MemberID LEFT OUTER JOIN
                      P_Sizes ON Order_Details.SizeID = P_Sizes.SizesID LEFT OUTER JOIN
                      P_Colors ON Order_Details.ColorID = P_Colors.ColorsID LEFT OUTER JOIN
                      T_Count ON Order_Details.CountID = T_Count.CountID LEFT OUTER JOIN
                      Factory ON Order_Master.FactoryID = Factory.FactoryID
WHERE     (Order_Master.OrderType = @orderType) AND (Order_Master.OrderMID = @OrderMID) AND (Order_Master.CompanyID = @CompanyID)
RETURN







