CREATE PROCEDURE [dbo].[spOTtime]
	@startDate DateTime,
	@endDate DateTime,
	@SectionID INT,
	@DesignationID INT,
	@FloorID INT,
	@LineID INT,
	@CompanyID int
AS
	 SET NOCOUNT ON 
	
	
Declare @MaxOT as INT


SET @MaxOT = isnull((SELECT top 1 MaxOverTime FROM SalarySheets WHERE IsDefault = 1 AND CompanyID = @CompanyID),0)


EXEC spGenerateDummyDate 	@startDate,@endDate;

SELECT     T.EmployeeID,
           T.CardNo, 
	       T.SectionName, 
	       T.Name,
		   T.dt AS AttDate,
		   MAX(Case WHEN isnull(I.OTHour,0.0) > @MaxOT AND @MaxOT > 0 Then @MaxOT  ELSE isnull(I.OTHour,0.0) END)*60 AS OT
		   --,MAX(I.entryTime) AS EntryTime, 
		   --MAX(I.exitTime)  AS ExitTime
		  ,T.CompanyID
		   
		FROM   (SELECT     vw_Employees.EmployeeID, vw_Employees.CardNo, vw_Employees.Name,vw_Employees.SectionName, vw_Employees.CompanyID, DummyDate.dt  FROM vw_Employees CROSS JOIN DummyDate WHERE (vw_Employees.SectionID = @SectionID OR @SectionID=0) AND (vw_Employees.CompanyID = @CompanyID) AND (vw_Employees.SectionID = @SectionID OR @SectionID = 0)
                 AND (vw_Employees.DesignationID = @DesignationID OR @DesignationID = 0) AND (vw_Employees.FloorID = @FloorID OR @FloorID = 0)  AND (vw_Employees.LineID = @LineID OR @LineID = 0)     ) AS T 
