CREATE PROCEDURE [dbo].[spOTtimeBySection]
	@startDate DateTime,
	@endDate DateTime,
	@SectionID INT,
	@DesignationID INT,
	@FloorID INT,
	@LineID INT,
	@CompanyID int
AS
	
	
Declare @MaxOT as INT


SET @MaxOT = isnull((SELECT top 1 MaxOverTime FROM SalarySheets WHERE IsDefault = 1 AND CompanyID = @CompanyID),0)


EXEC spGenerateDummyDate 	@startDate,@endDate;


SELECT  SectionName,
		COUNT(EmployeeID) AS TotalManpower,
		OT
FROM 
(SELECT T.EmployeeID AS EmployeeID,
	       T.SectionName AS SectionName, 
		   ROUND((Case WHEN max(isnull(I.OT,0.0)) > @MaxOT*60 AND @MaxOT > 0 Then MAX(@MaxOT*60 + DateDiff(hh,I.EntryTime,I.ExitTime) % 3) ELSE max(isnull(I.OT,0.0)) END)/60,0) AS OT		  

		   
		FROM   (SELECT     vw_Employees.EmployeeID, vw_Employees.CardNo, vw_Employees.Name,vw_Employees.SectionName, vw_Employees.CompanyID, DummyDate.dt  FROM vw_Employees CROSS JOIN DummyDate WHERE (vw_Employees.SectionID = @SectionID OR @SectionID=0) AND (vw_Employees.CompanyID = @CompanyID) AND (vw_Employees.SectionID = @SectionID OR @SectionID = 0)
                 AND (vw_Employees.DesignationID = @DesignationID OR @DesignationID = 0) AND (vw_Employees.FloorID = @FloorID OR @FloorID = 0)  AND (vw_Employees.LineID = @LineID OR @LineID = 0) AND vw_Employees.CompanyID = @companyID    ) AS T 
		LEFT OUTER JOIN ( select * from vw_AttInOut WHERE ( (vw_AttInOut.type) <> 'Holiday Present' AND @MaxOT > 0) OR @MaxOT = 0) I ON T.dt = I.attDate AND T.EmployeeID = I.EmployeeID 
