	@endDate DateTime,
	@sectionID int,
	@DesignationID int,
	@floorID int ,
	@lineID INT,
	@cardNO VARCHAR(50),
	@userID INT,
	@companyID INT
AS
	/* SET NOCOUNT ON */
	declare @leaveHoliday Varchar(100);
	set @leaveHoliday=isnull((SELECT RuleDefined.RuleCode FROM CompanyRule INNER JOIN RuleDefined ON CompanyRule.RuleID = RuleDefined.RuleID WHERE (RuleDefined.RuleGroup LIKE 'Holiday Between Leave' OR RuleDefined.RuleGroup LIKE 'Leave Between Holiday' ) AND (CompanyRule.CompanyID = @CompanyID)),'LB-002')
	--select @leaveHoliday
	--set xact_abort on;
    --begin tran;	
   
    
    
	EXEC spGenerateLeaveDays @startDate,@endDate,@sectionID, @DesignationID, @floorID,@lineID,@cardNO,@companyID,@userID
	
	
	EXEC spGenerateHoliDays @startDate,@endDate,@sectionID, @DesignationID, @floorID,@lineID,@cardNO,@companyID,@userID
	
	EXEC spGenerateHoliDayPresentDays @startDate,@endDate,@sectionID, @DesignationID, @floorID,@lineID,@cardNO,@companyID,@userID
	
	
	EXEC spGenerateLatePresentDays @startDate,@endDate,@sectionID, @DesignationID, @floorID,@lineID,@cardNO,@companyID,@userID


	EXEC spGenerateLunchAbsentDays @startDate,@endDate,@sectionID,@DesignationID, @floorID,@lineID,@cardNO,@companyID,@userID

	EXEC spGeneratePresentDays @startDate,@endDate,@sectionID, @DesignationID, @floorID,@lineID,@cardNO,@companyID,@userID

	--commit;
	
	IF @leaveHoliday ='LB-002' --Count as LeaveDay
	BEGIN
	DELETE FROM Holidays WHERE  (HolidayDate Between @startDate AND @endDate) AND (CompanyID=@CompanyID) AND EXISTS(SELECT LeaveDays.EmployeeID,LeaveDays.LeaveDate FROM LeaveDays WHERE LeaveDays.EmployeeID=Holidays.EmployeeID AND LeaveDays.LeaveDate=Holidays.HolidayDate   ) 
	END
	ELSE
	BEGIN
	DELETE FROM LeaveDays  WHERE (LeaveDate Between @startDate AND @endDate)AND (CompanyID=@CompanyID) AND EXISTS(SELECT Holidays.EmployeeID,Holidays.HolidayDate FROM Holidays WHERE Holidays.EmployeeID=LeaveDays.EmployeeID AND Holidays.HolidayDate=LeaveDays.LeaveDate   )
	END
	
	RETURN





