CREATE PROCEDURE [dbo].[spProcessBalanceSheet]
	@startDate DateTime,
	@endDate DateTime,
	@CompanyID INT,
	@ProfitFromPreviousYear Money, 
	@Dividend Money
AS
	/* SET NOCOUNT ON */
	
	Declare @ExportSale Money,@SellingExpense Money,@TurnOver Money,@ProductOverHead Money,@AdminOverHead Money, @FinancialExp Money,@OtherIncome Money;
	Declare @ExportSaleAccID INT,@SellingExpenseAccID INT,@ProductOverHeadID INT,@AdminOverHeadID INT, @FinancialExpID INT,@OtherIncomeID INT;
	
	Declare @RawPurchase Money,@FinishedPurchase Money,@GoodsSoldCost Money,@GrossProfit Money,@ProfitLossAccount Money,@ProfitLossAppr Money;
	Declare @RawPurchaseAccID INT,@FinishedPurchaseAccID INT;
	
	SET @ExportSaleAccID =(SELECT AccountID FROM T_ACCOUNT WHERE AccountTitle LIKE 'Export Sales' AND CompanyID=@CompanyID);
	SET @SellingExpenseAccID =(SELECT AccountID FROM T_ACCOUNT WHERE AccountTitle LIKE 'Selling Expense' AND CompanyID=@CompanyID);
	SET @ProductOverHeadID =(SELECT AccountID FROM T_ACCOUNT WHERE AccountTitle LIKE 'Production Overhead' AND CompanyID=@CompanyID);
	SET @RawPurchaseAccID =(SELECT AccountID FROM T_ACCOUNT WHERE AccountTitle LIKE 'Raw Materials Purchase' AND CompanyID=@CompanyID);
	SET @FinishedPurchaseAccID =(SELECT AccountID FROM T_ACCOUNT WHERE AccountTitle LIKE 'Finished Goods Purchase' AND CompanyID=@CompanyID);
	SET @AdminOverHeadID =(SELECT AccountID FROM T_ACCOUNT WHERE AccountTitle LIKE 'Administrative Overhead' AND CompanyID=@CompanyID);
	SET @FinancialExpID =(SELECT AccountID FROM T_ACCOUNT WHERE AccountTitle LIKE 'Financial Expense' AND CompanyID=@CompanyID);
	SET @OtherIncomeID =(SELECT AccountID FROM T_ACCOUNT WHERE AccountTitle LIKE 'Other Income Account' AND CompanyID=@CompanyID);
	
	SET @ExportSale = ISNULL((SELECT OpeningBalance FROM T_ACCOUNT WHERE AccountID=@ExportSaleAccID),0)
	                + ISNULL((SELECT SUM(DebitAmt - CreditAmt) as OpBal FROM VW_Transactions Where (TransDate BETWEEN @startDate AND @endDate) AND AccountID=@ExportSaleAccID),0);
	
	SET @SellingExpense = ISNULL((SELECT SUM(DebitAmt - CreditAmt) as OpBal FROM VW_Transactions INNER JOIN  VW_AccountWithClassification ON VW_Transactions.AccountID= VW_AccountWithClassification.AccountID Where (TransDate BETWEEN @startDate AND @endDate) AND AccountSubTypeID=@SellingExpenseAccID),0);
	
	SET @TurnOver = @ExportSale - @SellingExpense;
	
	SET @ProductOverHead = ISNULL((SELECT SUM(DebitAmt - CreditAmt) as OpBal FROM VW_Transactions INNER JOIN  VW_AccountWithClassification ON VW_Transactions.AccountID= VW_AccountWithClassification.AccountID Where (TransDate BETWEEN @startDate AND @endDate) AND AccountSubTypeID=@ProductOverHeadID),0);
	SET @AdminOverHead = ISNULL((SELECT SUM(DebitAmt - CreditAmt) as OpBal FROM VW_Transactions INNER JOIN  VW_AccountWithClassification ON VW_Transactions.AccountID= VW_AccountWithClassification.AccountID Where (TransDate BETWEEN @startDate AND @endDate) AND AccountSubTypeID=@AdminOverHeadID),0);
	SET @FinancialExp = ISNULL((SELECT SUM(DebitAmt - CreditAmt) as OpBal FROM VW_Transactions INNER JOIN  VW_AccountWithClassification ON VW_Transactions.AccountID= VW_AccountWithClassification.AccountID Where (TransDate BETWEEN @startDate AND @endDate) AND AccountSubTypeID=@FinancialExpID),0);
	SET @OtherIncome = ISNULL((SELECT SUM(DebitAmt - CreditAmt) as OpBal FROM VW_Transactions INNER JOIN  VW_AccountWithClassification ON VW_Transactions.AccountID= VW_AccountWithClassification.AccountID Where (TransDate BETWEEN @startDate AND @endDate) AND AccountSubTypeID=@OtherIncomeID),0);

	

		
	SET @RawPurchase = ISNULL((SELECT OpeningBalance FROM T_ACCOUNT WHERE AccountID=@RawPurchaseAccID),0)
	                + ISNULL((SELECT SUM(DebitAmt - CreditAmt) as OpBal FROM VW_Transactions Where (TransDate BETWEEN @startDate AND @endDate) AND AccountID=@RawPurchaseAccID),0);
	
	SET @FinishedPurchase = ISNULL((SELECT OpeningBalance FROM T_ACCOUNT WHERE AccountID=@FinishedPurchaseAccID),0)
	                + ISNULL((SELECT SUM(DebitAmt - CreditAmt) as OpBal FROM VW_Transactions Where (TransDate BETWEEN @startDate AND @endDate) AND AccountID=@FinishedPurchaseAccID),0);
	
	
	
	SET @GoodsSoldCost = @RawPurchase + @FinishedPurchase + @ProductOverHead ;
	
	SET @GrossProfit = @TurnOver - @GoodsSoldCost;
	
	SET @ProfitLossAccount = @GrossProfit + @OtherIncome - @AdminOverHead - @FinancialExp ;
	
	SET @ProfitLossAppr = @ProfitFromPreviousYear + @ProfitLossAccount - @Dividend;
	
	SELECT @startDate startDate ,@endDate endDate,@ProfitFromPreviousYear ProfitFromPreviousYear,@Dividend Dividend ,@ExportSale ExportSale,@SellingExpense SellingExpense ,@TurnOver TurnOver,@ProductOverHead ProductOverHead ,@AdminOverHead AdminOverHead, @FinancialExp FinancialExp ,@OtherIncome OtherIncome, @RawPurchase RawPurchase ,@FinishedPurchase FinishedPurchase ,@GoodsSoldCost GoodsSoldCost,@GrossProfit  GrossProfit,@ProfitLossAccount ProfitLossAccount,@ProfitLossAppr  ProfitLossAppr,@CompanyID CompanyID ;
	RETURN





GO

