CREATE PROCEDURE [dbo].[spRPTcustomSalarySheet]
	@CardNo varchar(50),
	@SectionID int,
	@startDate Datetime,
	@endDate DateTime,
	@SalarySheetID int,
	@CompanyID int
	
AS
	 SET NOCOUNT ON 
	 declare @EmpNO int;
	 set @EmpNO=(SELECT isnull(COUNT(Fns),0)+1 FROM vw_SalarySheetExecutablefunctions WHERE (SalarySheetID = @SalarySheetID) AND (CompanyID = @CompanyID));
	 
	Declare @IntegerFields Varchar(2000);
	SET @IntegerFields = (SELECT dbo.fnGetIntegerFieldsOfSalarySheet(@SalarySheetID,@CompanyID));
select X.*,@IntegerFields as IntegerFields ,@EmpNO as EmpNO from (
SELECT  VW_Employees.CardNo, 
			VW_Employees.Name, 
			VW_Employees.JoiningDate, 
			VW_Employees.SectionName AS Section, 
			VW_Employees.DesignationName AS Designation, 
            CustomeSalarySheet.SDate, 
            CustomeSalarySheet.EDate, 
            CustomeSalarySheet.Particulars, 
            CustomeSalarySheet.SValue,
            DisplayOrder,
            CustomeSalarySheet.functionName,
            CustomeSalarySheet.CompanyID,
            GradeOrScale
FROM   VW_Employees INNER JOIN
       CustomeSalarySheet ON VW_Employees.EmployeeID = CustomeSalarySheet.EmployeeID 
      
WHERE  (VW_Employees.CardNo = @CardNo OR  @CardNo = 'all') AND 
		(CustomeSalarySheet.SalarySheetID = @SalarySheetID) AND 
		(CustomeSalarySheet.SDate = @startDate) AND 
        (CustomeSalarySheet.EDate = @endDate) AND 
        (VW_Employees.SectionID = @SectionID OR @SectionID = 0) AND 
        (VW_Employees.CompanyID=@CompanyID)

UNION ALL
SELECT      VW_Employees.CardNo,
			VW_Employees.Name, 
			VW_Employees.JoiningDate, 
			VW_Employees.SectionName AS Section, 
			VW_Employees.DesignationName AS Designation, 
            CustomeSalarySheet.SDate, 
            CustomeSalarySheet.EDate, 
            'Net Pay' AS Particulars, 
           ROUND(SUM((CASE WHEN PaySlipAdd = '+' THEN 1 WHEN PaySlipAdd = '-' THEN - 1 ELSE 0 END) * SValue),0) AS SValue,
           MAX(DisplayOrder) + 1 AS DisplayOrder,
           'F_Net_Payable' AS FunctionName, 
           CustomeSalarySheet.CompanyID,GradeOrScale
FROM VW_Employees INNER JOIN CustomeSalarySheet ON VW_Employees.EmployeeID = CustomeSalarySheet.EmployeeID 
       
WHERE  (VW_Employees.CardNo = @CardNo OR  @CardNo = 'all') AND 
		(CustomeSalarySheet.SalarySheetID = @SalarySheetID) AND 
		(CustomeSalarySheet.SDate = @startDate) AND 
        (CustomeSalarySheet.EDate = @endDate) AND 
        (VW_Employees.SectionID = @SectionID OR @SectionID = 0) AND 
        (VW_Employees.CompanyID=@CompanyID)

GROUP BY VW_Employees.CardNo,VW_Employees.Name,VW_Employees.JoiningDate,VW_Employees.SectionName ,VW_Employees.DesignationName, SDate, EDate,CustomeSalarySheet.CompanyID,GradeOrScale
) AS X
ORDER by Section,CardNo

	RETURN





