CREATE PROCEDURE [dbo].[spRptDailyPresentWithOT]
	@attDate DATETIME,
	@CompanyID INT,
	@SectionID INT,
	@DesignationID INT,
	@FloorID INT,
	@LineID INT
AS

Declare @MaxOT as INT
SET @MaxOT = isnull((SELECT top 1 MaxOverTime FROM SalarySheets WHERE IsDefault = 1 AND CompanyID = @CompanyID),0)

SELECT     vw_Employees.CardNo, vw_Attendance.attDate, vw_Employees.Name, vw_Employees.DesignationName, vw_Employees.SectionName, 
           vw_Attendance.entryTime, 
           Case WHEN vw_Attendance.OT < vw_Attendance.OTStartAfter THEN vw_Attendance.exitTime   WHEN vw_Attendance.OT > @MaxOT*60 AND @MaxOT > 0 Then DateADD(minute, @MaxOT*60 +DateDiff(hh,vw_Attendance.EntryTime,vw_Attendance.ExitTime) % 3, vw_Attendance.EndTime ) ELSE vw_Attendance.exitTime END AS exitTime
           , vw_Attendance.StartTime, vw_Attendance.CompanyID, vw_Attendance.LateAllowTime, 
           vw_Attendance.OTStartAfter
            ,Case WHEN vw_Attendance.OT < vw_Attendance.OTStartAfter THEN 0 WHEN vw_Attendance.OT > @MaxOT*60 AND @MaxOT > 0 Then @MaxOT*60 + DateDiff(hh,vw_Attendance.EntryTime,vw_Attendance.ExitTime) % 3 ELSE vw_Attendance.OT END AS OT
           --, vw_Attendance.OT
           
FROM  vw_Attendance INNER JOIN  vw_Employees ON vw_Attendance.employeeID = vw_Employees.EmployeeID
WHERE vw_Employees.CompanyID = @CompanyID 
		  AND (vw_Employees.SectionID = @SectionID OR @SectionID = 0)
          AND (vw_Employees.DesignationID = @DesignationID OR @DesignationID = 0)
          AND (vw_Employees.FloorID = @FloorID OR @FloorID = 0)
          AND (vw_Employees.LineID = @LineID OR @LineID = 0)
AND  (vw_Attendance.attDate = @AttDate) 

ORDER BY vw_Employees.SectionName, vw_Employees.CardNo
	RETURN





