SET @ForBuyer = 0

 exec spGenerateDummyDate @startDate,@endDate;
 IF @ForBuyer = 0 
 BEGIN
 
	SELECT     @employeeID AS employeeID, D.dt AS attDate, MAX(A.entryTime) AS entryTime, MAX(A.exitTime) AS exitTime, MIN(ISNULL(A.Type, 'Absent')) AS type, 
						  A.OT
	FROM         DummyDate AS D LEFT OUTER JOIN
							  (SELECT     EmployeeID, attDate, entryTime, exitTime, (CASE WHEN Remarks LIKE '1' THEN 'Weekly ' WHEN Remarks IS NULL 
													   THEN '' ELSE 'National ' END) + type AS Type, OT
								FROM          vw_AttInOut
								WHERE      (EmployeeID = @employeeID) AND (attDate BETWEEN @startDate AND @endDate)) AS A ON D.dt = A.attDate
	GROUP BY A.EmployeeID, D.dt, A.OT
	ORDER BY attDate
END
ELSE
BEGIN
		
	SELECT     @employeeID AS employeeID, D.dt AS attDate, MAX(A.entryTime) AS entryTime, MAX(A.exitTime) AS exitTime, MIN(ISNULL(A.Type, 'Absent')) AS type, 
						  A.OT
	FROM         DummyDate AS D LEFT OUTER JOIN
							  (SELECT     EmployeeID, attDate, entryTime, exitTime, (CASE WHEN Remarks LIKE '1' THEN 'Weekly ' WHEN Remarks IS NULL 
													   THEN '' ELSE 'National ' END) + type AS Type, OT
								FROM          vw_AttInOut
								WHERE      (EmployeeID = @employeeID) AND (attDate BETWEEN @startDate AND @endDate)) AS A ON D.dt = A.attDate
	GROUP BY A.EmployeeID, D.dt, A.OT
	ORDER BY attDate
	END

