CREATE PROCEDURE [dbo].[spRptExportBill]
@startDate DateTime,
@endDate DateTime,
@CompanyID INT
	
AS
BEGIN

SELECT     T_Export_Bill.BillID, T_Export_Bill.BillType, T_Export_Bill.TypeNo, T_Ledgers.LedgerName, LC.LCNo, LC.TotalQty, LC.TotalValue, 
                      T_Export_Bill.BillDate, T_Export_Bill.BillAmount, T_Export_Bill.RealisedAmt, T_Export_Bill.LoanNo, T_Export_Bill.RealisedDate, 
                      T_Export_Bill.PurchaseDate, T_ExportBill_Invoices.InvoiceNo, T_ExportBill_Invoices.InvoiceValue, T_ExportBill_Invoices.InvoiceQty, 
                      T_ExportBill_Invoices.ExpNo
FROM         T_Export_Bill INNER JOIN
                          (SELECT DISTINCT T_LC_Master.LCID, T_LC_Master.LCNo, Order_Master.CustomerID, T_LC_Master.TotalQty, T_LC_Master.TotalValue
                            FROM          T_PI_Master INNER JOIN
                                                   T_LC_Master INNER JOIN
                                                   T_LC_Detail ON T_LC_Master.LCID = T_LC_Detail.LCID ON T_PI_Master.PIMID = T_LC_Detail.PIID INNER JOIN
                                                   T_PI_Details ON T_PI_Master.PIMID = T_PI_Details.PIMID INNER JOIN
                                                   Order_Master ON T_PI_Details.OrderID = Order_Master.OrderMID) AS LC ON T_Export_Bill.LCID = LC.LCID INNER JOIN
                      T_Ledgers ON LC.CustomerID = T_Ledgers.LedgerID INNER JOIN
                      T_ExportBill_Invoices ON T_Export_Bill.BillID = T_ExportBill_Invoices.BillID
WHERE     (T_Export_Bill.BillDate BETWEEN @startDate AND @endDate) AND (T_Export_Bill.CompanyID = @CompanyID)




END








