CREATE PROCEDURE [dbo].[spRPTExtraOT]
	@startDate DateTime,
	@endDate DateTime,
	@SectionID INT,
	@CompanyID INT
AS
	/* SET NOCOUNT ON */
	
Declare @MaxOT as INT
SET @MaxOT = isnull((SELECT top 1 MaxOverTime FROM SalarySheets WHERE IsDefault = 1 AND CompanyID = @CompanyID),0)	;

SELECT     vw_Employees.CardNo, 
		   vw_Employees.Name, 
		   vw_Employees.SectionName, 
		   A.attDate, 
		   CASE WHEN xtraOT > ISNULL(MaxExtraOTHour.MaxExtraOT,0) * 60 THEN ISNULL(MaxExtraOTHour.MaxExtraOT, 0)  ELSE xtraOT/60.0 END AS xtra
FROM 
(SELECT attDate, EmployeeID, (CASE WHEN ISNULL(OT, 0) > @MaxOT * 60 THEN ISNULL(OT, 0) - @MaxOT * 60 ELSE 0 END) AS xtraOT, CompanyID
                       FROM          vw_AttInOut
                       WHERE      (CompanyID = @CompanyID) AND (OT > 0) AND (attDate BETWEEN @startDate AND @endDate)) AS A INNER JOIN
                      vw_Employees ON A.EmployeeID = vw_Employees.EmployeeID LEFT OUTER JOIN
                      MaxExtraOTHour ON A.attDate = MaxExtraOTHour.OTDate AND A.CompanyID = MaxExtraOTHour.CompanyID
WHERE     (vw_Employees.SectionID = @SectionID)
ORDER BY vw_Employees.CardNo, A.attDate
	RETURN





GO

