CREATE PROCEDURE [dbo].[SpRptFestivalBonus]
	@StartDate datetime,
	@EndDate datetime,
	@MinJoinDate datetime,
	@MaxJoinDate datetime,
	@FloorID int,
	@LineID int,
	@DesignationID	int,
    @SectionID	int,
    --@SalaryFrom int,
    --@SalaryTo int, 
    @CompanyID	int
AS	
	SELECT     V.EmployeeID, V.CardNo, V.Name, V.JoiningDate, V.DesignationName, V.SectionName, V.FloorName,  
                      V.LineName, A.ApplyDate, (CASE WHEN IsFixed = 1 THEN FixedAmount ELSE dbo.AmountOnCalculationRule(A.EmployeeID,@StartDate,@EndDate,@CompanyID,Rate,CalcRuleID) END) AS Amount,
                     dateDiff(day,V.JoiningDate,@EndDate) AS SerDays
	FROM         vw_Employees AS V INNER JOIN
                      FestivalBonus AS A ON V.EmployeeID = A.EmployeeID
	WHERE     (A.ApplyDate >= @StartDate) AND (A.ApplyDate <= @EndDate) AND (V.DesignationID=@DesignationID OR @DesignationID=0) 
		  AND (V.SectionID=@SectionID OR @SectionID=0) AND (V.FloorID=@FloorID OR @FloorID=0) AND (V.LineID=@LineID OR @LineID=0) 
		  AND (V.JoiningDate >= @MinJoinDate) AND (V.JoiningDate <= @MaxJoinDate) AND (A.CompanyID = @CompanyID)
	
	RETURN





