CREATE PROCEDURE [dbo].[spRPTGetAttendance]
	@EmpCategoryID int,
	@DesignationID int,
	@SectionID int,
	@FloorID int,
	@LineID int,
	@cardNo varchar(50),
	@startDate DateTime,
	@endDate DateTime,
	@CompanyID int
	
AS
	 SET NOCOUNT ON 
	 
	
	
	exec  spGenerateDummyDate @startDate,@endDate;
	
	 SELECT     E.EmployeeID, E.CardNo, E.Name, E.CategoryName, E.DesignationName, 
                      E.SectionName, E.FloorName, E.LineName, E.EmpTypeName, E.dt as attDate ,A.attendanceID, 
                      /*A.attDate,*/ A.entryTime /*,@defaultInTime)as entryTime*/ , A.exitTime /*,@defaultOutTime) as exitTime*/,ES.ScheduleID,ES.dayEnd
FROM
(
select vw_Employees.*, dummyDate.dt from vw_Employees  cross join dummyDate
WHERE
(
(cardNo=@cardNo or @cardNo='all') AND
(EmpCategoryID=@EmpCategoryID or @EmpCategoryID=0) AND
(DesignationID=@DesignationID or @DesignationID=0) AND
(SectionID=@SectionID or @SectionID=0) AND
(FloorID =@FloorID or @FloorID=0) AND
(LineID=@LineID or @LineID=0)
) 
AND  CompanyID=@CompanyID
) E 

LEFT OUTER JOIN
 ( select Attendance.* from Attendance  where (attDate between @startDate and @endDate) and (CompanyID=@CompanyID)) A 
 ON E.EmployeeID = A.employeeID and E.dt=A.attDate
 INNER JOIN (select EmployeeShift.employeeID,EmployeeShift.ScheduleID,EmployeeShift.effectivefrom,EmployeeShift.effectiveto,Schedule.dayEnd from EmployeeShift inner join Schedule ON EmployeeShift.ScheduleID=Schedule.ScheduleID)as ES 
 ON (ES.EmployeeID=E.EmployeeID and E.dt between ES.effectivefrom and ES.effectiveto)
 
 Order By CardNo,dt

                      

	RETURN





