CREATE PROCEDURE [dbo].[SpRptIncrement]
	@StartDate datetime,
	@EndDate datetime,
	@SectionID int,
	@EmployeeID int,
	@TransType int,
	@CompanyID int
AS
	/*SET NOCOUNT ON */
	SELECT     EmployeeHistory.EmployeeID, vw_Employees.CardNo, vw_Employees.Name, vw_Employees.JoiningDate, vw_Employees.DesignationName, 
                      vw_Employees.SectionName, vw_Employees.GradeOrScale, vw_Employees.JoiningSalary, (CASE WHEN IsFixed = 1 THEN EmployeeHistory.Salary - IncrAmount ELSE EmployeeHistory.Salary / (1 + IsNull(Rate, 0) / 100) END) 
                      AS PrevSalary, EmployeeHistory.IsFixed, EmployeeHistory.IncrAmount, EmployeeHistory.Rate, EmployeeHistory.Salary AS CurrSalary, 
                      EmployeeHistory.effectiveDate, EmployeeHistory.DecisionDate,EmployeeHistory.ModifiedDate, DATEDIFF(day, vw_Employees.JoiningDate, 
                      EmployeeHistory.effectiveDate) AS ServiceDays,vw_Employees.Salary AS PresentSalary,EmployeeHistory.Remarks
                      
    FROM       EmployeeHistory INNER JOIN
                      vw_Employees ON EmployeeHistory.EmployeeID = vw_Employees.EmployeeID
    WHERE     (vw_Employees.SectionID = @SectionID OR @SectionID = 0) AND (EmployeeHistory.EmployeeID = @EmployeeID OR @EmployeeID = 0) AND (EmployeeHistory.effectiveDate BETWEEN @StartDate AND
                       @EndDate) AND (EmployeeHistory.TransType  =@TransType) AND (EmployeeHistory.EmpStatusID=1) AND (EmployeeHistory.CompanyID = @CompanyID)
    ORDER BY   vw_Employees.SectionName, vw_Employees.CardNo,EmployeeHistory.ModifiedDate,EmployeeHistory.effectiveDate
	RETURN





