CREATE PROCEDURE [dbo].[SpRptInOutTime]
	@startDate DateTime,
	@endDate DateTime,
	@SectionID INT,
	@DesignationID INT,
	@FloorID INT,
	@LineID INT,
	@CompanyID int
AS
	
Declare @MaxOT as INT

SET @MaxOT = isnull((SELECT top 1 MaxOverTime FROM SalarySheets WHERE IsDefault = 1 AND CompanyID = @CompanyID),0)

EXEC spGenerateDummyDate 	@startDate,@endDate;



if @MaxOT > 0  ---for buyer
begin
SELECT     T.EmployeeID,
           T.CardNo, 
	       T.SectionName, 
	       T.Name,
		   T.dt AS AttDate,
		   MAX(I.entryTime) AS EntryTime, 
		   MAX(CASE WHEN isnull(I.OT, 0.0) <= @MaxOT * 60 THEN I.exitTime ELSE   dateadd(minute,(- isnull(I.OT, 0)+ DateDiff(hh,I.EntryTime,I.ExitTime) % 3 +@MaxOT*60 ),(I.exitTime)) END) AS ExitTime
		   , T.CompanyID
		   
		FROM   (SELECT     vw_Employees.EmployeeID, vw_Employees.CardNo, vw_Employees.Name,vw_Employees.SectionName, vw_Employees.CompanyID, DummyDate.dt  FROM vw_Employees CROSS JOIN DummyDate WHERE (vw_Employees.SectionID = @SectionID OR @SectionID = 0) AND (vw_Employees.DesignationID = @DesignationID OR @DesignationID = 0) AND (vw_Employees.FloorID = @FloorID OR @FloorID = 0) AND (vw_Employees.LineID = @LineID OR @LineID = 0) AND (vw_Employees.CompanyID = @CompanyID)     ) AS T 
		LEFT OUTER JOIN ( select * from vw_AttInOut WHERE (vw_AttInOut.type) <> 'Holiday Present' ) I ON T.dt = I.attDate AND T.EmployeeID = I.EmployeeID
		
		GROUP BY T.EmployeeID, T.CardNo, T.Name, T.SectionName, T.dt, T.CompanyID
		ORDER BY T.CardNo, T.SectionName, T.dt, T.CompanyID
end
else
begin
SELECT     T.EmployeeID,
           T.CardNo, 
	       T.SectionName, 
	       T.Name,
		   T.dt AS AttDate,
		   
		   MAX(I.entryTime) AS EntryTime, 
		   
		  MAX(I.exitTime) AS ExitTime,
		   T.CompanyID
		   
		FROM   (SELECT     vw_Employees.EmployeeID, vw_Employees.CardNo, vw_Employees.Name,vw_Employees.SectionName, vw_Employees.CompanyID, DummyDate.dt  FROM vw_Employees CROSS JOIN DummyDate WHERE (vw_Employees.SectionID = @SectionID OR @SectionID = 0) AND (vw_Employees.DesignationID = @DesignationID OR @DesignationID = 0) AND (vw_Employees.FloorID = @FloorID OR @FloorID = 0) AND (vw_Employees.LineID = @LineID OR @LineID = 0) AND (vw_Employees.CompanyID = @CompanyID)     ) AS T 
		LEFT OUTER JOIN  vw_AttInOut AS I ON (T.dt = I.attDate AND T.EmployeeID = I.EmployeeID)
		
		GROUP BY T.EmployeeID, T.CardNo, T.Name, T.SectionName, T.dt, T.CompanyID
		ORDER BY T.CardNo, T.SectionName, T.dt, T.CompanyID
end

	
	RETURN




GO

