CREATE PROCEDURE [dbo].[SpRptInsurance]
	@Status varchar(50),
	@StartDate datetime,
	@EndDate datetime,
	@SectionId int,
	@FloorID int,
	@LineID int,
	@DesignationID int,
	@CompanyID int 
AS
	SET NOCOUNT ON 
	IF @Status = 'Active'
	BEGIN
	SELECT     A.EmployeeID, A.CardNo, A.Name, A.JoiningDate, A.DateOfBirth, A.DesignationName, A.SectionName, B.FathersName, A.EmpStatusName
    FROM       vw_EmployeesAll AS A LEFT OUTER JOIN EmployeeDetails AS B ON A.EmployeeID = B.EmployeeID
    WHERE     (A.DesignationID = @DesignationID OR @DesignationID = 0) AND (A.SectionID = @SectionID OR @SectionID = 0)  AND (A.FloorID = @FloorID OR @FloorID = 0)
          AND (A.LineID = @LineID OR  @LineID = 0) AND (A.CompanyID = @CompanyID) AND (A.EmpStatusName LIKE @Status) 
	END
	ELSE IF @Status = 'Inactive'
    BEGIN
    SELECT     A.EmployeeID, A.CardNo, A.Name, A.JoiningDate, A.DateOfBirth, A.DesignationName, A.SectionName, B.FathersName, A.EmpStatusName
    FROM       vw_EmployeesAll AS A LEFT OUTER JOIN EmployeeDetails AS B ON A.EmployeeID = B.EmployeeID
    WHERE     (A.DesignationID = @DesignationID OR @DesignationID = 0) AND (A.SectionID = @SectionID OR @SectionID = 0)  AND (A.FloorID = @FloorID OR @FloorID = 0)
          AND (A.LineID = @LineID OR  @LineID = 0)  AND (A.CompanyID = @CompanyID) AND (A.EmpStatusName NOT LIKE 'Active') 
    END
	ELSE IF @Status = 'New'
	BEGIN
	SELECT     A.EmployeeID, A.CardNo, A.Name, A.JoiningDate, A.DateOfBirth, A.DesignationName, A.SectionName, B.FathersName, A.EmpStatusName
    FROM       vw_EmployeesAll AS A LEFT OUTER JOIN EmployeeDetails AS B ON A.EmployeeID = B.EmployeeID
    WHERE     (A.DesignationID = @DesignationID OR @DesignationID = 0) AND (A.SectionID = @SectionID OR @SectionID = 0)  AND (A.FloorID = @FloorID OR @FloorID = 0)
          AND (A.LineID = @LineID OR  @LineID = 0)  AND (A.CompanyID = @CompanyID) AND (A.JoiningDate BETWEEN @StartDate AND @EndDate) 
	END
	RETURN





GO

