CREATE PROCEDURE [dbo].[spRPTjobCard]
	@CardNo varchar(50),
	@SectionID int,
	@startDate DateTime,
	@endDate DateTime,
	@CompanyID int
AS
SET NOCOUNT ON 

Declare @BuyerOT AS INT 



EXEC spGenerateDummyDate 	@startDate,@endDate;

Declare @MaxOT as INT
Declare @OTDeduction as INT


SET @MaxOT = isnull((SELECT top 1 MaxOverTime FROM SalarySheets WHERE IsDefault = 1 AND CompanyID = @CompanyID),0)

SET @OTDeduction =isnull((SELECT top 1 CompanyRule.ParamValue  FROM CompanyRule INNER JOIN RuleDefined ON CompanyRule.RuleID =RuleDefined.RuleID WHERE RuleDefined.RuleCode = 'Ex-001'),0)

IF @MaxOT > 0  /* Buyer*/
	BEGIN
		SELECT     T.CardNo, 
				   T.Name, 
				   T.JoiningDate, 
				   T.GradeOrScale, 
				   T.DesignationName, 
				   T.SectionName, 
				   T.FloorName, 
				   T.LineName, 
				   @MaxOT AS MaxOT,
				   0 /*@OTDeduction*/ AS OTDeduction,
				   T.dt AS AttDate,
				   MAX( CASE WHEN isnull(I.OT,0) >@MaxOT*60 THEN @MaxOT*60 + DateDiff(hh,I.EntryTime,I.ExitTime) % 3 ELSE I.OT END) as BOT ,
				   CAST( max(isnull(I.OT,0.0) + DateDiff(hh,I.EntryTime,I.ExitTime) % 3 ) as Float) AS OT,
				   mAX(I.LeaveType) as LeaveType, 
				   mAX( I.LateMin) as LateMin, 
				   MIN(ISNULL(I.type, 'Absent')) AS Type,    
				   max(I.remarks) as remarks,           
				   MAX(I.entryTime) AS EntryTime, 
				   MAX(I.exitTime)  AS ExitTime
				   ,MAX(CASE WHEN isnull(I.OT, 0.0) <=@MaxOT *60 THEN I.exitTime ELSE   dateadd(minute,-( isnull(I.OT, 0)- DateDiff(hh,I.EntryTime,I.ExitTime) % 3 -@MaxOT*60 ),(I.exitTime)) END) AS BExitTime
				   
				   --CASE WHEN isnull(max(vw_AttInOut.OT),0.0)/60.0 <=@MaxOT THEN MAX(vw_AttInOut.exitTime) ELSE  dateadd(minute,-( isnull(max(vw_AttInOut.OT),0)-@MaxOT*60  ),MAX(vw_AttInOut.exitTime)) END AS ExitTime
		FROM   (SELECT     vw_Employees.EmployeeID, vw_Employees.CardNo, vw_Employees.Name, vw_Employees.JoiningDate, vw_Employees.GradeOrScale, vw_Employees.DesignationName, vw_Employees.SectionName, vw_Employees.FloorName, vw_Employees.LineName, vw_Employees.CompanyID, DummyDate.dt  FROM vw_Employees CROSS JOIN DummyDate WHERE (vw_Employees.CardNo = @CardNo OR @CardNo='all') AND (vw_Employees.SectionID=@SectionID OR @SectionID = 0) AND (vw_Employees.CompanyID = @CompanyID)     ) AS T 
		LEFT OUTER JOIN ( select * from vw_AttInOut WHERE (vw_AttInOut.type) <> 'Holiday Present') I ON T.dt = I.attDate AND T.EmployeeID = I.EmployeeID
		
		GROUP BY T.CardNo, T.Name, T.JoiningDate, T.GradeOrScale, T.DesignationName, T.SectionName, T.FloorName, T.LineName, T.dt /*,  vw_AttInOut.OT, vw_AttInOut.LeaveType, vw_AttInOut.LateMin*/
		ORDER BY T.dt
	END
ELSE    /* Actual */
	BEGIN
		SELECT     T.CardNo, 
				   T.Name, 
				   T.JoiningDate, 
				   T.GradeOrScale, 
				   T.DesignationName, 
				   T.SectionName, 
				   T.FloorName, 
				   T.LineName, 
				   @MaxOT AS MaxOT,
				   0 AS OTDeduction,
				   T.dt AS AttDate,
                   max( CASE WHEN (isnull(vw_AttInOut.OT,0.0) - @OTDeduction) >0 AND DateDiff(day,EntryTime,ExitTime) = 0 THEN (isnull(vw_AttInOut.OT,0.0) - @OTDeduction) ELSE Case WHEN DateDiff(day,EntryTime,ExitTime) > 0 THEN isnull(vw_AttInOut.OT,0.0) ELSE 0 END END) AS BOT,
