				   MAX(CASE WHEN (isnull(vw_AttInOut.OT,0.0) - @OTDeduction) >0 AND DateDiff(day,EntryTime,ExitTime) = 0 THEN DATEADD(mi, - @OTDeduction, vw_AttInOut.exitTime) ELSE vw_AttInOut.exitTime END) AS BExitTime
