CREATE PROCEDURE [dbo].[spRptOpeningBalanceOfAccount]
	@UpToDate DateTime,
	@AccountID INT
	
AS
	/* SET NOCOUNT ON */
	Declare @OpBalance Money;
	SET @OpBalance =ISNULL((SELECT Nature*OpeningBalance FROM T_Account WHERE AccountID= @AccountID),0)+ISNULL((SELECT ISNULL(SUM(DebitAmt - CreditAmt), 0) AS OpBal FROM VW_Transactions WHERE (TransDate < @UpToDate) AND (AccountID = @AccountID)),0);
	
