CREATE PROCEDURE [dbo].[SpRptPromotion]
	@StartDate datetime,
	@EndDate datetime,
	@SectionID int,
	@EmployeeID int,
	@TransType int,
	@CompanyID int
AS
	SELECT     Section.Name AS SectionName,Employee.CardNo, Employee.Name, Employee.JoiningDate, DATEDIFF(day, Employee.JoiningDate, 
                      EmployeeHistory.effectiveDate) AS ServiceDays,dbo.fnLastDesignation(EmployeeHistory.EmployeeID,@TransType,EmployeeHistory.effectiveDate,EmployeeHistory.ModifiedDate) AS prevDesSec , Designation.Name AS NewDes, 
                      EmployeeHistory.ModifiedDate, EmployeeHistory.effectiveDate,EmployeeHistory.DecisionDate, JoiningSalary.Salary AS joiningSalary,(CASE WHEN IsFixed = 1 THEN EmployeeHistory.Salary - IncrAmount ELSE EmployeeHistory.Salary / (1 + IsNull(Rate, 0) / 100) END) 
                      AS PrevSalary,EmployeeHistory.Salary AS CurrSalary,Present.Salary AS PresentSalary,Present.DesName AS PresentDes,Present.SectionName AS PresentSection,Present.Grade AS PresentGrade,SpAllowance.SpA,EmployeeHistory.Remarks
	FROM       EmployeeHistory LEFT OUTER JOIN
                      Designation ON EmployeeHistory.DesignationID = Designation.DesignationID LEFT OUTER JOIN
                      Section  ON EmployeeHistory.SectionID = Section.SectionID INNER JOIN
                      Employee ON EmployeeHistory.EmployeeID = Employee.EmployeeID LEFT OUTER JOIN                      
                      (SELECT     EmployeeID, MAX(Salary) AS Salary
                       FROM          dbo.EmployeeHistory AS EmployeeHistory_1
                       WHERE      (TransType = 0) OR
                                  (TransType = 1) GROUP BY EmployeeID) AS JoiningSalary 
                               ON JoiningSalary.EmployeeID = EmployeeHistory.EmployeeID  INNER JOIN  
                      (SELECT     EmployeeHistory_2.EmployeeID,EmployeeHistory_2.EmpStatusID, Section.Name AS SectionName,Designation.Name AS DesName,PayScale.GradeOrScale AS Grade,Salary
                       FROM       EmployeeHistory AS EmployeeHistory_2 INNER JOIN Employee ON EmployeeHistory_2.EmployeeHistoryID = Employee.EmployeeHistoryID
                                                                       INNER JOIN Section ON EmployeeHistory_2.SectionID = Section.SectionID
                                                                       INNER JOIN Designation ON EmployeeHistory_2.DesignationID = Designation.DesignationID
                                                                       INNER JOIN PayScale ON EmployeeHistory_2.PayScaleID = PayScale.PayScaleID
                       ) AS Present
                               ON Present.EmployeeID = EmployeeHistory.EmployeeID LEFT OUTER JOIN
                      (SELECT    EmployeeID, Amount AS SpA, ApplyDate 
					   FROM         OtherAllowance
					   WHERE     (ApplyDate BETWEEN @StartDate AND @EndDate) AND (AllowanceName LIKE 'Special Allowance')
                       ) AS SpAllowance
                               ON SpAllowance.EmployeeID = EmployeeHistory.EmployeeID AND SpAllowance.ApplyDate=EmployeeHistory.EffectiveDate
	WHERE     (Section.SectionID = @SectionID OR
                      @SectionID = 0) AND (EmployeeHistory.TransType = @TransType)
                      AND (EffectiveDate BETWEEN @StartDate AND @EndDate)
                      AND (EmployeeHistory.EmployeeID = @EmployeeID OR @EmployeeID = 0)
                      AND (Present.EmpStatusID=1)
                      AND (EmployeeHistory.CompanyID = @CompanyID)
    ORDER BY   Section.Name, Employee.CardNo,EmployeeHistory.ModifiedDate,EmployeeHistory.effectiveDate
	RETURN





