CREATE PROCEDURE [dbo].[spRptSalarySectionMonth]
	@SectionID int,
	@FloorID int,
	@startDate DateTime,
	@endDate dateTime,	
	@CompanyID int
	
AS
	SET NOCOUNT ON 
	SELECT     EmployeeID, CardNo, EmployeeName, SectionName, FloorName, LineName, Production, Bonus, NoWorkSalary, 
                      Production + Bonus + NoWorkSalary AS ProductionSalary, ISNULL(NoWorkDay, 0) AS NoWorkDay, ISNULL(Attendence, 0) AS Attendence
FROM         
(
             SELECT     derivedtbl_1.EmployeeID, derivedtbl_1.CardNo, derivedtbl_1.EmployeeName, derivedtbl_1.SectionName, derivedtbl_1.FloorName, 
             derivedtbl_1.LineName, derivedtbl_1.Production,ISNULL(P_ProductionBonus.FixedBonus + P_ProductionBonus.PercentBonus * derivedtbl_1.Production / 100, 0) AS Bonus, 
             ISNULL(derivedtbl_1.NoWorkSalary * P_NoWorkDays.NoWorkDay, 0) AS NoWorkSalary, P_NoWorkDays.NoWorkDay, derivedtbl_2.Attendence
      FROM   (SELECT     EmployeeID, CardNo, Name AS EmployeeName, SectionName, FloorName, LineName, ISNULL(SUM(ReceivedQty * Rate), 0) AS Production, NoWorkSalary
                  FROM          vw_EmpProcessQty
                  WHERE      (ReceivedDate BETWEEN @StartDate AND @EndDate) AND (SectionID=@SectionID OR @SectionID =0) AND (FloorID=@FloorID OR @FloorID =0) AND CompanyID=@CompanyID 
                  GROUP BY EmployeeID, CardNo, SectionName, FloorName, LineName, Name, NoWorkSalary) 
              AS derivedtbl_1 
      INNER JOIN
             P_ProductionBonus ON derivedtbl_1.Production >= P_ProductionBonus.LowestProdAmount AND derivedtbl_1.Production <= P_ProductionBonus.HighestProdAmount 
      LEFT OUTER JOIN
             (SELECT     employeeID, ISNULL(COUNT(*), 0) AS Attendence
                  FROM          Attendance
                  WHERE      (attDate BETWEEN @StartDate AND @EndDate)
                  GROUP BY employeeID) AS derivedtbl_2 ON derivedtbl_1.EmployeeID = derivedtbl_2.employeeID 
      LEFT OUTER JOIN
             P_NoWorkDays ON derivedtbl_1.EmployeeID = P_NoWorkDays.EmployeeID AND P_NoWorkDays.EffectiveMonth BETWEEN @StartDate AND @EndDate
 ) 
 AS DERIVED
	RETURN
		




GO

