CREATE PROCEDURE [dbo].[spRptTrialBalanceDetails]
	@atDate datetime,
	@CompanyID int

AS

Declare @FYID INT ; 
SET @FYID = ISNULL((SELECT FiscalYearID FROM FiscalYear  WHERE (companyID = @companyID) AND (@atDate BETWEEN startdate AND ISNULL(enddate, @atDate))),0);

IF @FYID =0
BEGIN

 RAISERROR ('No Fiscal Year Created', 16, 1)
END

Declare @CrBalance Money, @DrBalance Money, @DiffInOP Money;

SET @CrBalance =ISNULL((SELECT SUM(ISNULL(B.OpeningBalance,0)) FROM  VW_AccountWithClassification A INNER JOIN VW_FiscalYearAccountBalance B ON A.AccountID=B.AccountID WHERE B.FYID=@FYID AND (Nature = - 1) AND (A.CompanyID = @CompanyID) AND (ISNULL(AccountSubType,'') NOT LIKE  'Stock & Store')),0);
SET @DrBalance =ISNULL((SELECT SUM(ISNULL(B.OpeningBalance,0)) FROM  VW_AccountWithClassification A INNER JOIN VW_FiscalYearAccountBalance B ON A.AccountID=B.AccountID WHERE B.FYID=@FYID AND  (Nature = 1) AND (A.CompanyID = @CompanyID) AND (ISNULL(AccountSubType,'') NOT LIKE  'Stock & Store')),0);

SET @DiffInOP = @CrBalance - @DrBalance;

Declare @StartDateFY DateTime;
SET  @StartDateFY=(SELECT startDate FROM FiscalYear WHERE FiscalYearID=@FYID AND (companyID = @companyID))
IF @StartDateFY IS NULL
BEGIN
Declare @str Varchar(100);
SET @str = 'No Fiscal Year Created';
 RAISERROR (@str, 16, 1)
END

SELECT * FROM (
SELECT T.AccountID,A.AccountTitle,A.AccountType, A.AccountSubType, A.AccountMinorType,SUM(OpBal) as OpeningBalance  FROM
	(
	select A.AccountID,Nature*ISNULL(B.OpeningBalance,0) as OpBal FROM VW_AccountWithClassification A INNER JOIN VW_FiscalYearAccountBalance B ON A.AccountID=B.AccountID WHERE B.FYID=@FYID AND AccOrGroup Like 'Account' AND A.CompanyID=@CompanyID AND (ISNULL(AccountSubType,'') NOT LIKE  'Stock & Store')
	UNION ALL
	SELECT AccountID,(DebitAmt - CreditAmt) as OpBal FROM VW_Transactions Where (TransDate BETWEEN @StartDateFY AND @atDate) AND CompanyID=@CompanyID 
	) T INNER JOIN VW_AccountWithClassification A ON T.AccountID=A.AccountID
	GROUP BY T.AccountID,A.AccountTitle,A.AccountType, A.AccountSubType, A.AccountMinorType --ORDER BY A.AccountTitle
UNION ALL 
SELECT NULL,'Diff. In Opening Balance','Diff. In Opening Balance','','',	@DiffInOP  WHERE @DiffInOP <> 0 	
) X
WHERE OpeningBalance <>0 ORDER BY AccountTitle,AccountType, AccountSubType,AccountMinorType
	RETURN









GO

