CREATE PROCEDURE [dbo].[SpSaveORUpdateOTRule]
@EmployeeOTRuleID	int OUTPUT	,
@EmployeeID	int	,
@OTRate	money	,
@EffectiveDate	datetime	,
@Type	int	,
@FixedAmount	money	,
@Times	money	,
@MonthlyOTHour	money	,
@CompanyID	int	,
@UserID	int	,
@ModifiedDate	datetime	

AS

IF @Type = 1
	  SET @OTRate = (@Times* (SELECT dbo.F_Basic_Salary(@EmployeeID, getDate(),GETDATE(),@CompanyID)))/@MonthlyOTHour
ELSE IF @Type = 2
	  SET @OTRate = (@Times* (SELECT dbo.F_Gross_Salary(@EmployeeID, getDate(),GETDATE(),@CompanyID)))/@MonthlyOTHour
ELSE IF @Type = 3
	  SET @OTRate = (@Times* @FixedAmount)/@MonthlyOTHour



IF @EmployeeOTRuleID = 0
BEGIN
		DELETE EmployeeOTRule WHERE EmployeeID = @EmployeeID;
	INSERT INTO EmployeeOTRule
							  ( EmployeeID, EffectiveDate, CompanyID, UserID, ModifiedDate, Type, FixedAmount, Times, MonthlyOTHour, OTRate)
		VALUES     (@EmployeeID,@EffectiveDate,@CompanyID,@UserID,@ModifiedDate,@Type,@FixedAmount,@Times,@MonthlyOTHour, @OTRate)
END
ELSE
Begin

	UPDATE    EmployeeOTRule
	SET              EmployeeID = @EmployeeID, EffectiveDate = @EffectiveDate, CompanyID = @CompanyID, 
						  UserID = @UserID, ModifiedDate = @ModifiedDate, Type = @Type, FixedAmount = @FixedAmount, Times = @Times, 
						  MonthlyOTHour = @MonthlyOTHour, OTRate = @OTRate
	WHERE     (EmployeeOTRuleID = @EmployeeOTRuleID)
	

SET @EmployeeOTRuleID = @@IDENTITY

End
	RETURN





