	@FriendlyName varchar(200),
	@CanEdit int, 
	@CanDelete int, 
	@CanAdd int, 
	@CanView int, 
	@CompanyID int
AS
	Declare @Count int
	SET @Count=	(Select COUNT(*)  FROM RolePrivilege WHERE (Role = @Role) and (CompanyID = @CompanyID)  AND (FriendlyName=@FriendlyName))
	IF @Count=0
		INSERT INTO RolePrivilege(Role, FriendlyName, CanEdit, CanDelete, CanAdd, CanView, CompanyID)
                                      VALUES     (@Role, @FriendlyName, @CanEdit, @CanDelete, @CanAdd, @CanView, @CompanyID)
    ELSE
		UPDATE    RolePrivilege SET  CanEdit= @CanEdit, CanDelete= @CanDelete, CanAdd= @CanAdd, CanView= @CanView
                                     WHERE     FriendlyName= @FriendlyName AND (CompanyID = @CompanyID) AND (Role = @Role)
	RETURN





