CREATE PROCEDURE [dbo].[spSaveUpdateBillAdjust]
	@BillAdjustID INT,
	@BillsID INT,
	@AdjustDate DateTime,
	@AdjustAmount Money,
	@AdjustAccID INT,
	@AdjustMethodID INT,
	@AdjustRefNo Varchar(100),
	@AdjustRefLCID INT,
	@Remarks Varchar(500),
	@TransRefID Int,
	@TransVoucherNo Varchar(100),
	@AcceptDate DateTime,
	@MaturityDate DateTime,
	@BillAccID INT=NULL,
	@CurrencyID INT=NULL,
	@CurrencyRate Money=1
AS
	/* SET NOCOUNT ON */
IF @BillAdjustID = 0	
BEGIN
UPDATE  T_Bills SET DueAmount=DueAmount - @AdjustAmount WHERE (BillsID = @BillsID);
INSERT INTO T_Bills_Adjust
                      (BillsID, AdjustDate, AdjustAmount, AdjustAccID, AdjustMethodID, AdjustRefLCID,AdjustRefNo, Remarks,TransRefID,TransVoucherNo,AcceptDate,MaturityDate,BillAccID,CurrencyID,CurrencyRate)
VALUES     (@BillsID,@AdjustDate,@AdjustAmount,@AdjustAccID,@AdjustMethodID,@AdjustRefLCID,@AdjustRefNo,@Remarks,@TransRefID,@TransVoucherNo,@AcceptDate,@MaturityDate,(CASE WHEN @BillAccID <=0 THEN NULL ELSE @BillAccID END),(CASE WHEN @CurrencyID <=0 THEN NULL ELSE @CurrencyID END),@CurrencyRate)

END
ELSE
BEGIN
Declare @PrevAdj Money;
SET @PrevAdj=ISNULL((SELECT AdjustAmount FROM T_Bills_Adjust WHERE (BillAdjustID = @BillAdjustID) ),0);

UPDATE  T_Bills SET DueAmount=DueAmount - (@AdjustAmount-@PrevAdj)  WHERE (BillsID = @BillsID);

UPDATE    T_Bills_Adjust
SET              BillsID = @BillsID, AdjustDate = @AdjustDate, AdjustAmount = @AdjustAmount, AdjustAccID = @AdjustAccID, AdjustMethodID = @AdjustMethodID, 
         AdjustRefLCID=@AdjustRefLCID,AdjustRefNo = @AdjustRefNo, Remarks = @Remarks,TransRefID=@TransRefID,TransVoucherNo=@TransVoucherNo,AcceptDate=@AcceptDate,MaturityDate=@MaturityDate
		,BillAccID=(CASE WHEN @BillAccID <=0 THEN NULL ELSE @BillAccID END),CurrencyID=(CASE WHEN @CurrencyID <=0 THEN NULL ELSE @CurrencyID END),CurrencyRate=@CurrencyRate	
WHERE     (BillAdjustID = @BillAdjustID)


END
	RETURN






