CREATE PROCEDURE [dbo].[spSaveUpdateLoanAdjust]
	@LoanAdjustID int,
	@loanID int,
	@AdgustmentDate datetime,
	@AdjustAmount money,
	@AdjustMethodID int,
	@AdjustRefNo varchar(50),
	@Remarks varchar(50),
	@PayFrom INT,
	@TransRefID INT
AS
	DECLARE @Interest Money;
	SET @Interest = (SELECT  dbo.fnGetInterestOfLoan( @loanID,@AdgustmentDate));

	if @LoanAdjustID=0
	BEGIN
	UPDATE T_Loan SET DueAmount=DueAmount + @Interest - @AdjustAmount WHERE LoanID = @loanID;

	insert into T_LoanAdjust (LoanID,AdjustDate,AdjustAmount,AdjustMethodID,AdjustRefNo,Remarks,PayFrom,TransRefID)
	values
	(@loanID,@AdgustmentDate,@AdjustAmount,@AdjustMethodID,@AdjustRefNo,@Remarks,@PayFrom,@TransRefID)
	END
	else
	BEGIN
	DECLARE @PreAdjuastAmt MONEY;
	SET @PreAdjuastAmt=ISNULL((SELECT AdjustAmount FROM T_LoanAdjust WHERE (LoanAdjustID=@LoanAdjustID) ),0);
	UPDATE T_Loan SET DueAmount=DueAmount + @Interest -(@AdjustAmount-@PreAdjuastAmt) WHERE LoanID = @loanID;

	Update T_LoanAdjust set LoanID=@loanID,AdjustDate=@AdgustmentDate,AdjustAmount=@AdjustAmount,AdjustMethodID=@AdjustMethodID,AdjustRefNo=@AdjustRefNo,Remarks=@Remarks,PayFrom=@PayFrom,TransRefID=@TransRefID
